INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Terra Carmesim", "Usando sua Arma Mecânica de Perfuração em seu braço direito, Toki dispara na direção que ele deseja com tanta força que ele é capaz de literalmente mudar a geografia da área afetada. Ele usou esta técnica para criar uma bifurcação do rio que era usada como um ponto de estrangulamento, onde ele podia atacar seus alvos.", "Todos os alcances", null, null, "土紅", "Tsuchi Kurenai", "Terra Carmesim", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Terra Queimada da Busca da Verdade", "O usuário reúne as Esferas da Busca da Verdade de suas costas e libera seu poder em um feixe de energia que causa graves danos ao oponente.", "Todos os alcances", null, null, "求道焦土ぐどうしょうど", "Gudō Shōdo", "Terra Queimada da Busca da Verdade", "Calor do Caminho da Verdade", null, null);
