INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tigela do Gato de Fogo", "Yugito libera uma de suas caudas e começa a girar, criando um envoltório de fogo azul em torno de si e se transformando em Matatabi. Com a besta no controle, ela levanta sua pata e bate com força no chão, arranhando o oponente e enviando-o junto com várias bolas de fogo para trás, as quais se são guadas contra o inimigo, resultando numa explosão azul cintilante generalizada.", "Longo alcance", null, null, "猫火鉢", "Nekohibachi", "Tigela do Gato de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tigre Diurno", "O Tigre Diurno, contrariando sua aparência, é puramente taijutsu na natureza e é nada mais do que um punho de pressão de ar extremamente rápido conforme Might Guy observou.", "Curto a Médio alcance", "Rank A", "Sho → Selo especifico da técnica", "昼虎ひるどら", "Hirudora", "Tigre Diurno", null, "Tigre da Tarde", null);
