INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro Aquático", "O usuário exala uma bola de água de sua boca e a lança em alta velocidade em direção ao seu alvo. O impacto da esfera é forte o suficiente para jogar seu oponente a vários metros.", "Médio alcance", null, "Passaro", "アクアシュート", "Akua Shūto", "Tiro Aquático", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro Preparado de Agulhas", "Usando um dispositivo montado no pulso, o usuário pode lançar agulhas em seu oponente. O dispositivo é geralmente usado para surpreender o inimigo, como ele pode ser escondido sob mangas compridas. As agulhas também podem ser mergulhadas em um veneno mortal, aumentando sua letalidade.", "Curto a Médio alcance", "Rank C", null, "仕込針弾", "Shikomishindan", "Tiro Preparado de Agulhas", "Barragem de Agulhas de Veneno", "Agulhas Envenenadas", "Arte Ninja: Agulhas de Veneno");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro da Chama Explosiva", "Com a criação de uma faísca de suas mãos, esta técnica permite que o usuário jogue várias bolas de fogo, que são capazes de causar ou explosões de fogo com o impacto ou definir o alvo em chamas.", "Curto à Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro de Agulha na Névoa de Veneno", "Shizune utiliza primeiro a Névoa Venenosa em seu oponente e, em seguida, ataca com seus bisturi de chakra algumas vezes. Finalmente, ela salta no ar e joga agulhas em seu oponente.", null, null, null, null, null, null, "Bala de Agulha da Névoa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro de Fogo", "Com o auxílio da Lâmina do Dragão, Naruto dispara uma grande bola de fogo contra seu oponente. Quando lançada a partir do ar, a técnica se ramifica em múltiplas esferas menores que caem sobre o alvo.", "Curto a Médio alcance", null, null, null, null, null, "Tiro de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro de Muco Explosivo", "Katsuyu libera uma pasta amarela e ácida de sua boca no chão, que se eleva criando uma formação semelhante à uma parede, capaz de danificar um alvo ou fornecer uma defesa eficaz.", "Todos os alcances", null, null, "爆液放射", "Bakueki Hōsha", "Tiro de Muco Explosivo", "Explosão de Muco", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tiro do Mecanismo Destrutivo", "O usuário usa sua marionete para atirar várias kunai com veneno a partir de sua boca em seu oponente.", "Todos os alcances", null, null, "カラクリ散弾破", "Karakuri Sandan Yabu", "Tiro do Mecanismo Destrutivo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Todas as Armas Acima do Céu", "Esta técnica aumenta todos os aumentos de status (Velocidade, Ataque, Defesa) para todos os personagens em combate com Tenten.", "Curto alcance", null, null, null, null, null, "Todas as Armas Acima do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tonfa Voadora", "Empunhando suas tonfas, Kokuyō joga uma delas em alta velocidade, criando uma áurea de vácuo em torno da mesma e uma onda de impacto devido à resistência do vento. A força do impacto é tão grande que é capaz de arrancar o topo de um morro e bloquear a passagem dos inimigos. Depois de cumprir o trajeto designado, a tonfa volta para as mãos de Kokuyō, como um bumerangue.", "Longo alcance", null, null, null, null, null, null, null, null);
