INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tornado Alto", "Após acertar em seu oponente vários socos e chutes com um clone das sombras, Boruto impulsiona-se liberando um tornado de vento abaixo de seus pés e chuta seu oponente, jogando-o para o alto.", "Curto alcance", null, null, "トルネイドハイ", "Toruneido Hai", "Tornado Alto", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tornado Negro", "Os membros da Família Kurosuki sobem um em cima do outro, formando um pilar de cinco pessoas, e em seguida, mostram lâminas retráteis de suas luvas e levantam seus braços. Eles, então, começam a girar, criando um ataque semelhante à um tornado negro. A velocidade faz com que o ataque seja tão poderoso que as lâminas podem facilmente cortar pedras.", null, null, null, "黒竜巻", "Kuro Tatsumaki", "Tornado Negro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tornado Relâmpago", "Tornado Relâmpago (旋風雷光撃, Torunēdo Raitoningu) é uma técnica utilizada por Ranke ao extrair energia da Pedra de Gelel.", "Curto a Médio alcance", null, null, "旋風雷光撃せんぷうらいこうげき", "Torunēdo Raitoningu", "Ataque Relâmpago do Redemoinho de Vento", null, null, null);
