INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tornado de Vento Direto", "Ao balançar suas asas, o Kuchiyose Final é capaz de criar um enorme tornado de vento capaz de empurrar um humano ou mesmo desferir pequenos cortes.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tornado de Água", "Essa técnica utiliza água para criar um turbilhão de água em volta do usuário. O turbilhão de água atua como uma barreira tanto defensiva como ofensiva. O poder do turbilhão é poderoso o suficiente para deixar o oponente inconsciente.", "Curto a Médio alcance", null, "Carneiro → Cavalo → Passaro", "水竜巻", "Mizu Tatsumaki", "Tornado de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tornado do Dragão Relâmpago", "Empunhando suas duas espadas juntas, Raiga cria-os no ar e atira um raio para baixo em cima de seu corpo. A corrente é contida dentro e ao redor de Raiga. Ele, então, começa a girar no lugar formando um vórtice rodopiante de vento e eletricidade. O vórtice assume a forma de uma cabeça de dragão e cobre a área em frente a ele. Então ataca contra o seu adversário e dolorosamente prende-o no vórtice. Mesmo que o ataque não se conecte diretamente com seu inimigo, o relâmpago pode saltar do vórtice para atingir um alvo próximo.", "Curto a Médio alcance", null, null, "雷竜の竜巻", "Rairyū no Tatsumaki", "Tornado do Dragão Relâmpago", null, null, "Arte Ninja: Tornado Relâmpago do Dragão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Torpedo de Pregos", "Gosunkugi lança inúmeros pregos ao ar, que caem, e, então, cria uma área cheia de pregos no solo. Uma vez que inimigo não pode enxergar os pregos e anda pela área, Gosunkugi performa um selo de mão e puxa os pregos de volta, a fim de perfurar o inimigo.", "Curto a Médio alcance", null, "Dragao", "魚雷針ぎょらいしん", "Gyoraishin", "Torpedo de Pregos", null, null, "Arte Ninja: Pregos Torpedo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Torção da Busca da Verdade", "Madara ataca o oponente com as Esferas da Busca da Verdade antes de transformá-las em uma broca extremamente mortal e perfurá-lo brutalmente.", "Curto a Médio alcance", null, null, "捩求道ねじぐどう", "Nejigudō", "Torção da Busca da Verdade", "Deformação da Busca da Verdade", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Torção da Flor", "Kimimaro, enquanto em seu selo amaldiçoado nível dois, usa sua cauda para executar um forte golpe no inimigo, podendo derrubá-lo e cortá-lo com os ossos de sua cauda.", "Curto alcance", null, null, "捩じり花", "Nejirihana", "Torção da Flor", "Suspensão da Flor, Flor Furiosa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trabalho em Equipe Excepcional", "Naruto, Sakura, e Sasuke chutam seu inimigo em um ataque combinado. Naruto e Sasuke então correm, acertando uma série de combos no alvo. Aproveitando o momento, Sakura revela um fio de aço que ela tem em seus equipamentos e faz um circulo no chão para usá-lo à fim de apertar o adversário, porém, acidentalmente ela também captura Naruto, enquanto Sasuke escapa usando a Técnica de Substituição do Corpo.", "Curto alcance", null, null, "チームワークは抜群", "Chīmuwāku wa Batsugun", "Trabalho em Equipe Excepcional", "Trabalho de Equipe", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Traje Majestoso: Susanoo", "O Traje Majestoso: Susanoo é uma armadura especial extremamente resistente, que pode ser moldada de acordo com a vontade e a necessidade do utilizador. O usuário utiliza a armadura que normalmente seria usada para equipar seu Susanoo, para outras finalidades. Essa armadura é resistente o suficiente de aguentar uma Bola da Besta com Cauda, e consegue resistir parcialmente a uma Bola da Besta com Cauda contendo Senjutsu. Essa armadura é muito eficaz contra tentativas de absorção de chakra e contra técnicas de supressão.", "Curto alcance", "Rank –", null, "威装・須佐能乎", "Isō: Susanoo", "Traje Majestoso: Ele com a Capacidade de Ajudar em Todos os Meios", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transferência de Chakra", "Esta é uma técnica que permite ao usuário que transfira parte de seu chakra para outra pessoa.", null, null, "Carneiro[2]", "チャクラ送", "Chakura Tensō", "Transferência de Chakra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação Combinada", "Uma forma avançada da Técnica de Transformação, o Konbi Henge (コンビ変化, Técnica de Transformação Combinada) transforma dois usuários em um único ser. Em alguns casos as etapas são divididas, enquanto um usuário executa os selos de mão necessários, o outro pode fornecer o chakra. Naruto utilizou este método com Gamabunta em sua luta contra Gaara (quando ele usou a forma completa do Shukaku) para assumir a aparência de uma raposa gigante (Kurama no anime). O Quarteto do Som usou essa técnica durante o Exame Chūnin para se disfarçarem como guarda-costas do Yondaime Kazekage (dois em cada). Kiba e Akamaru criaram a sua própria variação desta técnica com o Jinjū Konbi Henge: Sōtōrō.", null, "Rank B", "Carneiro invertido", "コンビ変化", "Konbi Henge", "Técnica de Transformação Combinada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação Combinada da Besta Humana: Lobo de Duas Cabeças", "Uma transformação combinada, onde homens e animais se tornam uma única entidade. É uma especialidade do clã Inuzuka, um clã que tem andado lado a lado com ninken por gerações. Eles se fundem em um lobo bicéfalo, assim aumentando drasticamente o seu poder. Em seus ataques diretos, eles demonstram grande eficiência. Além disso, utilizando técnicas nessa forma dá origem á um extraordinário poder de destruição.", null, "Rank B", null, "人獣混合変化・双頭狼", "Jinjū Konbi Henge: Sōtōrō", "Transformação Combinada da Besta: Lobo de Duas Cabeças", null, "Transformação e Combinação de Animal e Ser Humano: Lobo de Duas Cabeças", "Combo de Transformação Homem-Animal: Lobo de Duas Cabeças");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação Misturada da Besta Humana — Lobo de Três Cabeças", "Esta técnica é uma especialidade do clã Inuzuka, seguindo os princípios do clã de utilização de parceiros ninken. Depois de criar um clone da sombra, o usuário combina com este clone e seu companheiro ninken em um lobo gigante, de três cabeças. Isso aumenta drasticamente o seu tamanho e poder, ao mesmo tempo, ganhando garras afiadas e dentes caninos. Em seus ataques diretos, eles demonstram considerável eficiência. Além disso, usando técnicas para além disso dá origem a uma força destrutiva extraordinária.", null, "Rank –", "Tigre", "人獣混合変化・参頭狼", "Jinjū Kongō Henge — Santōrō", "Transformação da Mistura da Besta Humana — Lobo de Três Cabeças", null, "Estilo Inuzuka: Transformação e Combinação de Animal e Ser Humano: Lobo de Três Cabeças", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação: Bastão Adamantino", "A técnica de transformação do Rei Macaco, Enma, é caracterizada pela dureza do diamante e de sua capacidade à vontade para estender e/ou expandir o Bastão Adamantino, que é a arma de escolha de Hiruzen Sarutobi. Este bastão, quando em uso, foi capaz de destruir facilmente as estruturas de madeira criadas por Hashirama, um usuário de Liberação de Madeira, e também para esmagar Kurama, mandando-o para as fronteiras de Konoha, e destruir os galhos da Árvore Divina.", null, null, null, "変化・金剛如意へんげ・こんごうにょい", "Henge: Kongōnyoi", "Transformação: Bastão Adamantino", null, "Transformação: Bastão Indestrutível", "Bastão Adamantino");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação do Ataque Suicida de Vento Demoníaco", "Naruto cria um clone para jogar o oponente no ar onde o Naruto real surge com uma fūma shuriken que corta seu oponente.", "Curto alcance", null, null, "風魔特攻変化", "Fūma Tokkō Henge", "Transformação do Ataque Suicida de Vento Demoníacoto", "Transformação da Carga do Demônio de Vento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação do Clone das Sombras", "A Transformação do Clone das Sombras é uma técnica que emprega o uso da Técnica dos Clones das Sombras e da Técnica de Transformação. Essa técnica é extremamente útil, uma vez que pode ser utilizada para confundir e enganar seus inimigos, ou até mesmo criar armas para serem usadas em combate.", null, null, null, "影分身の変化", "Kage Bunshin no Henka", "Transformação do Clone das Sombras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transmissão Relâmpago", "Usando o auxílio de um clone, cada um dos dois Kakashi realizam a técnica Corte Relâmpago em mãos opostas. Conectando as suas técnicas em conjunto, eles formam uma corrente de relâmpagos entre si que é capaz de cortar através de objetos como braços de chakra de bestas com cauda com grande facilidade. Kakashi também foi capaz de envolver o relâmpago em torno de um alvo, assim restringindo efetivamente os seus movimentos, como mostrado contra o Estátua Demoníaca do Caminho Exterior.", "Curto a Médio alcance", null, null, "雷伝らいでん", "Raiden", "Transmissão Relâmpago", null, "Laço Relâmpago", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transmissão da Mente Psicótica", "O usuário entra no subconsciente do alvo, colocando a mão sobre a cabeça do alvo e extraindo todas as informações necessárias, mesmo se o alvo se esquecesse ou tivesse um bloqueio. Um dispositivo especial pode ser utilizado para diminuir o tempo necessário para ler a mente do alvo e permite que outros ninjas ajudem. Memórias que têm bloqueios mentais sobre elas vão exigir longos períodos de tempo para sondar através e obter no entanto. Dependendo da habilidade do usuário e seu nível de experiência, eles serão capazes de passar dias de memórias do alvo em questão de momentos e peneirar até encontrar o que querem, enquanto outros podem ter que passar por cada memória sua totalidade.", "Curto alcance", null, null, "催戸伝心", "Saiko Denshin", "Transmissão de Exploração-Porta Mente", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transmissão de Madeira", "Depois de transformar um clone de madeira em uma semente, a semente pode ser usada para rastrear um alvo ao colocada nele, ou na ocasião de o alvo a ingerir. Somente o usuário dessa técnica pode detectar transmissões da semente.", null, null, null, "送信木そうしき", "Sōshinki", "Transmissão de Madeira", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transmissão de Sensoriamento", "Ao fazer contato físico com outra pessoa, o usuário é capaz de transmitir o que estão sentindo atualmente diretamente na mente do aliado.", "Curto alcance", "Rank –", null, "感知伝々かんちでんでん", "Kanchi Denden", "Transmissão de Sensoriamento", "Comunicação Sensorial", "Transferência de Percepção", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transportando os Corpos", "Kazuma usa essa técnica para controlar os caixões de Kitane, Nauma, Seito e Tōu.", "Todos os alcances", null, "Controlar: Cachorro → Cobra → Porco,Enterrar: Cobra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transversal Cintilante", "Shisui agarra seu oponente e o chuta para o alto, movendo-se em alta velocidade, onde ele desfere uma série de cortes com sua tantō.", "Curto a Médio alcance", null, null, "瞬断", "Shundan", "Transversal Cintilante", "Divisão Instantânea", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tratamento Petriquímico", "O Tratamento Petroquímico é um processo médico onde usuário reúne chakra medicinal em uma esfera branca com brilho azul-esverdeado e o move sobre as áreas feridas do corpo. Sakura usou esta técnica tentando curar Kakeru Tsuki, que foi petrificado pela manopla de Ishidate, mas como já fazia muito tempo desde que ele foi petrificado, ela não foi capaz de curar as feridas e salvar o rei.", "Curto alcance", null, "Rato → Carneiro → Cao", "石化治療せっかちりょう", "Sekka Chiryō", "Tratamento Petriquímico", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tremor Violento do Rato de Relâmpago", "Após performar alguns selos de mão, Nigai libera uma descarga de eletricidade que dispara vários discos destrutivos de eletricidade. O potencial desta técnica não foi mostrado, uma vez que ela foi anulada antes que pudesse mostrar seu efeito.", null, null, "Macaco → Boi → Javali → Tigre", "雷鼠激震らいそげきしん", "Raiso Gekishin", "Tremor Violento do Rato de Relâmpago", null, null, "Onda de Choque do Rato Relâmpago");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trifoliata", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente com sua espada de osso, bate no oponente com sua cauda e finaliza batendo no oponente com seus ossos nas costas, jogando-o para trás.", "Curto alcance", null, null, "カラタチ", "Karatachi", "Trifoliata", "Laranja Trifoliata", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trifoliata (Ultimate Ninja 3)", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente com sua espada de osso, bate no oponente com sua cauda e finaliza batendo no oponente com seus ossos nas costas, jogando-o para trás.", "Curto alcance", null, null, "カラタチ", "Karatachi", "Trifoliata", "Laranja Trifoliata", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trifoliata (Ultimate Ninja Storm Generations)", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente no ar com suas espadas de osso e, em seguida, libera ossos por toda parte do corpo, atacando-o em forma circular irregular e finalizando com um chute para baixo na cabeça do oponente.", "Curto alcance", null, null, "カラタチ", "Karatachi", "Trifoliata", "Pônciros", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trindade", "Obito usa a Liberação de Fogo: Técnica da Fênix Sábia de Fogo e Minato joga algumas Kunai do Deus Voador do Trovão no oponente. Kakashi aproveita e forma um Chidori, dando tempo para Obito ativar seu Sharingan e Minato teleportar os dois usando a Técnica do Deus Voador do Trovão. Kakashi acerta o oponente com seu Chidori e Obito chuta o oponente, em seguida, Kakashi dá uma breve corrida e volta com um novo Chidori que acerta novamente o oponente. Minato então se teleporta até o oponente e os três atacam em conjunto, Obito esfaqueando com uma kunai, Minato cortando o oponente com sua kunai e Kakashi com o Sabre de Chakra da Luz Branca.", "Curto a Médio alcance", null, null, "三位一体さんみいったい", "Sanmiittai", "Trindade", "Força-Tripla", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trio Impecável", "Kakashi, Obito e Rin avançam em direção ao oponente. Kakashi dá alguns cortes no oponente com sua Sabre de Chakra da Luz Branca, Obito acerta um golpe de kunai e Rin chuta o adversário. Enquanto o oponente está atordoado, Rin torce por Kakashi e Obito. Em seguida, Obito atira várias bolas de fogo contra o oponente e Kakashi finaliza com mais um corte de seu Sabre de Chakra da Luz Branca.", "Curto a Médio alcance", null, null, "完全無欠の三人かんぜんむけつのさんにん", "Kanzen Muketsu no Sannin", "Trio Impecável", "Trio Perfeito", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Triplo Mecanismo da Borda", "Kankurō manipula o Corvo para lançar uma barragem de kunai envenenadas através do seu peito até o oponente simultaneamente com o Formiga Negra lançando bombas de fumaça venenosas.", "Curto alcance", null, null, null, null, "Triplo Mecanismo da Borda", "Técnica Secreta de Marionetes: Barragem da Kunai Envenenada", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trovoada", "Sasuke encobre-se de relâmpago, empunha sua espada e rapidamente ataca o oponente repetidas vezes cortando-o e eletrocutando-o, deixando rastros de relâmpago por onde passa, finalizando com um Chidori e incinerando o oponente no final.", "Curto alcance", null, null, "迅雷", "Jinrai", "Trovoada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trovão Enfurecido", "Ao focar sua energia Gelel em sua espada e depois de dar um golpe com ela, Temujin pode criar uma barreira poderosa Gelel diante dele. Está técnica foi utilizada na tentativa de anular o Rasengan de Naruto. Os dois ataques resultam em uma enorme explosão que destruiu o pico da montanha onde Naruto e Temujin estavam lutando.", "Curto alcance.", null, null, "魔導昇雷撃", "Reijingu Sandā", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trovão dos Mortos", "Orochimaru invoca uma cobra e Sasuke usa a Corrente de Chidori e em espiral em torno deles pode atingir o oponente se nas proximidades.", "Curto alcance", null, null, "亡者の雷", "Mōja no Kaminari", "Trovão dos Mortos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Trunfo em Dupla", "Naruto cria vários clones das sombras para empurrar, segurar o oponente e, em seguida, chutá-lo para o alto. Em queda, o oponente é atingido certeiramente por uma combinação entre o Rasengan de Naruto com o Chidori de Sasuke.", "Curto alcance", null, null, "二人の切り札ふたりのきりふだ", "Futari no Kirifuda", "Trunfo em Dupla", "Último Recurso Duplo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Truque do Gato", "Ao acessar seu poder de gato, Wasabi salta e desfere um golpe vertical no chão. Em seguida, ela desfere outro golpe com a outra mão, a fim de criar uma cortina de poeira e usar o impulso para escapar do local. Estes movimentos foram o suficiente para cobrir sua retirada com Tsubaki de modo a seus adversários não as notarem.", "Curto alcance", null, null, "猫騙しねこだまし", "Nekodamashi", "Truque do Gato", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Três Colorações", "Kurenai libera um pilar de pétalas de cerejeira no oponente enviando-o para longe do local.", "Curto alcance", null, null, "三彩", "Sansai", "Três Colorações", "Chute de Coloração da Placa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Três Deuses Leais", "Momoshiki agarra seu oponente pelo pescoço e o lança para o alto e, em seguida, ele se transforma ao consumir uma Panaceia Cinábrio. Já transformado, ele bate uma mão no chão e conjura cinco grandes cães que mordem o oponente, deixando-o suspenso no ar, criando abertura para que o Faisão Caçador acerte o alvo em cheio, criando uma explosão ao atingi-lo. Para finalizar, ele cria um golem de rocha fundida que, ao comando raivoso de seu conjurador, desfere um poderoso soco.", "Todos os alcances", null, null, "三忠神さんちし", "Sanchūjin", "Três Deuses Leais", "Ameaça Tripla", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tsukuyomi", "Tsukuyomi é notado por ser um dos genjutsu mais poderosos que existem. Diz-se representar o 'Mundo Espiritual e a Escuridão' (精神界と闇, Seishinkai to Yami), o antípode de Amaterasu — um ninjutsu de poder similar.", "Curto alcance", null, null, "月読", "Tsukuyomi", "Leitor da Lua", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tsukuyomi Infinito", null, "Todos os alcances", "Rank –", "Liberar: Rato ou Juntar as Maos[2][Nota 1]", "無限月読まげんつくよみ", "Mugen Tsukuyomi", "Leitor da Lua Infinito", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tubarão Ameaçador", "Kisame usa a Liberação de Água: Onda de Choque Explosiva de Água e corta o oponente debaixo d'água com a Samehada.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tubarão Perfurador", "Kisame, fundido com a Samehada, pula no ar e começa a rotacionar em torno de si, usando suas barbatanas adquiridas para raspar e cortar o oponente, absorvendo seu chakra, graças à habilidade da espada.", "Curto alcance", null, null, "穿鮫", "Senkō", "Tubarão Perfurador", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Turbilhão Ardente", "O Dez-Caudas cria uma bola de fogo gigante através de um dos apêndices com olhos ao longo de seu corpo e lança-a para cima. Quando a bola de fogo cai no chão, ela cria um enorme turbilhão em chamas, que é grande o suficiente para ultrapassar a altura do Dez-Caudas em seu estágio inicial. Esse turbilhão foi poderoso o suficiente para engolir e derrubar Kurama por algum tempo, causando grande dano.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Turbilhão Varredor", "Quando transformado em Kokuō, Han ataca seu oponente pela frente, e por todos os outros lados cardeais diversas vezes, atingindo o oponente com seus chifres e girando suas caudas criando um ataque poderoso em seu oponente.", "Curto alcance", null, null, "旋風払い", "Senpū Harai", "Turbilhão Varredor", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Adamantina: Triturador de Crânio", "Hiruzen salta em cima da elevação de terra que fez, para alcançar o adversário. Em seguida, ele invoca o Rei Macaco: Enma e ataca com ele duas vezes, finalizando com um ataque do seu bastão na cabeça do inimigo.", "Curto alcance", null, null, "金剛術・頭蓋砕きこんごうじゅつ・ずがいくだき", "Kongōjutsu: Zugai Kudaki", "Técnica de Vajra: Triturador de Crânio", "Jutsu Adamantino: Esmagador de Crânio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Branca Secreta Reversa: Coleção de Uematsu de Dez Marionetes", "Depois de tomar o controle do corpo de dez indivíduos, o usuário os controla para atacar de vários lados ao mesmo tempo, como uma formação, deixando pouco ou nenhum tempo para reagir.", "Todos os alcances", null, null, "裏白秘技・十機植松の集うらしろひぎ・じゅっきうえまつのしゅう", "Ura Shirohigi: Jukki Uematsu no Shū", "Técnica Branca Secreta Reversa: Coleção de Uematsu de Dez Marionetes", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu", "Depois de desenrolar o pergaminho, a coleção de Chikamatsu aparece. Usando a Técnica de Marionetes simples, pode-se controlar à vontade, controlando dez marionetes ao mesmo tempo em cada dedo, com um poder correspondente a mil bonecos juntos. Exige conhecimentos de alta-classe e habilidade, para conseguir controlá-los em seu auge de poder.", "Todos os alcances", "Rank S", null, "白秘技・十機近松の集", "Shirohigi: Jikki Chikamatsu no Shū", "Técnica Branca Secreta: A Coleção de Chikamatsu das Dez Marionetes", "Dez Marionetes de Chikamatsu", "Técnica Branca Secreta: Dez Obras-Primas de Chikamatsu", "Ataque Branco Secreto: As Dez Marionetes de Chikamatsu");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu: Ataque da Terra", "Usando as marionetes de Monzaemon Chikamatsu, Chiyo usa o braço da marionete 4 e a lâmina de chakra da marionete 3 para lançar a marionete 5 longe, concluindo nela empalando o adversário com os cabos presentes em sua cabeça.", "Todos os alcances", null, null, "白秘技・十機近松の集・地の攻", "Shirohigi: Jikki Chikamatsu no Shū: Chi no Kō", "Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu: Ataque da Terra", "Dez Marionetes: Ataque da Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Especial de Projeção da Shuriken de Sakura", "Sakura lança nove shuriken em direção à seu oponente, que estão ligadas à fios de chakra o que ajuda na perseguição de seu inimigo.", "Curto à Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Hemostática", "A Técnica Hemostática é um ninjutsu médico utilizado durante procedimentos cirúrgicos para estancar hemorragias. Ao término da cirurgia, a abertura feita durante o procedimento é fechada.", "Curto alcance", null, null, "止血術", "Shiketsu Jutsu", "Técnica Hemostática", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Ninja de Ocultação", "Esta é uma habilidade geral que permite que um ninja esconda-se dentro de uma caixa onde torna-o invisível aos olhos de quem vê. Esta habilidade simples, embora pareça inútil, mostrou-se ter certa relevância, visto que aqueles que observam a caixa, a examina como um simples objeto inanimado, tornando irrelevante e dando possibilidade de fuga ao ninja que encontra-se dentro da mesma.", null, null, null, "尾行忍術びこうにんじゅつ", "Bikō Ninjutsu", "Técnica Ninja de Ocultação", null, null, "Arte Ninja: Sombreamento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Pachinko", "Naruto e Sasuke jogam um objeto duro no adversário com força suficiente para derrubá-lo. Eles realizaram esta técnica usando o Chakra Líquido de Jako que ligava os seus braços juntos, como um estilingue para lançar uma estátua de ouro em Jako.", "Longo alcance", null, null, "パチンコの術", "Pachinko no Jutsu", "Técnica Pachinko", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Parasita", "Enquanto em tamanho reduzido, Isshiki adentra o corpo de seu alvo e ali instala-se como parasita. Dentro do hospedeiro, ele começa a absorver nutrientes para sobreviver, e, sequencialmente, a controlar sua mente, falar com sua voz, usar seus braços e pernas, então assumindo ao controle de seu corpo para sempre.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Espetáculo de Controle de Chamas do Escorpião", "Utilizando o Lança-Chamas presente na marionete Sasori, o usuário faz com que um fluxo de fogo seja lançado contra o oponente, podendo controlar sua intensidade. No anime, esse fogo é quente o suficiente incinerar totalmente uma kunai e um corpo em questão de segundos embora grandes pedaços maciços de pedras sofram apenas derretidos superficialmente.", "Curto a Médio alcance", null, null, "黒秘技・操炎蝎劇", "Kurohigi: Sōen Katsugeki", "Técnica Preta Secreta: Espetáculo de Controle de Chamas do Escorpião", "Movimento Secreto Negro: Show do Escorpião de Controle de Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Ferro Negro", "Shinki captura seu alvo com areia de ferro e então invoca sua marionete que o morde, desencadeando uma fórmula de fūinjutsu para selar seus movimentos temporariamente enquanto o enterra em meio à areia de ferro.", "Curto a Médio alcance", null, null, "黒秘技・黒鉄­", "Kurohigi: Kurogane", "Técnica Preta Secreta: Ferro Negro", null, null, "Arte Ninja Secreta: Aço Negro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Magnífico Arremate em Chamas", "Kankurō manipula sua marionete Salamandra para liberar uma enorme explosão incinerante ou um fluxo de fogo contra o oponente.", "Curto alcance", null, null, "黒秘技・炎填華", "Kurohigi: Entenka", "Técnica Preta Secreta: Magnífico Arremate em Chamas", "Movimento Secreto Negro: Flor de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Maquinário de Tiro Duplo", "A versão aprimorada da Técnica Preta Secreta: Maquinário de Tiro Único que não exige Karasu. Kankurō manobra Kuroari atrás do alvo, que então se abre o peito e dispara para fora fios de chakra que envolvem seu alvo. Em seguida, puxa o alvo na sua cavidade abdominal aberta. Com a vítima presa, foices como lâminas aparecem e ceifam através de um lado da marionete para o outro, matando a vítima dentro.", "Curto alcance", null, null, "黒秘技・機々二発", "Kurohigi: Kiki Nihatsu", "Técnica Preta Secreta: Maquinário de Tiro Duplo", null, null, "Técnica Secreta Tenebrosa da Segunda Dama de Ferro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Maquinário de Tiro Triplo", "Esta técnica é uma versão em maior escala da Técnica Preta Secreta: Maquinário de Tiro Único adaptada para dois ou três alvos. Kankurō controla de duas a três marionetes Formiga Negra para capturar os inimigos. Ele, então, desmonta o Corvo para empalar as vítimas, mas também pode contar com o auxílio de um aliado para atacar um dos cativos.", "Curto alcance", "Rank A", null, "黒秘技・機々三発くろひぎ・ききさんぱつ", "Kurohigi: Kiki Sanpatsu", "Técnica Preta Secreta: Maquinário de Tiro Triplo", null, "Técnica Negra das Fincadas de Versão Tripla", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Maquinário de Tiro Único", "Primeiro, o adversário é preso no tronco de Kuroari. Depois disso, Karasu se separa em oito compartimentos. O tronco é deixado para trás, enquanto os braços, pernas e cabeça ficam posicionados ao redor de Kuroari, cada um com uma lâmina afiada. Kankurō manipula as peças separadas em fendas no tronco de Kuroari, espetando e envenenando a vítima no interior.", "Curto alcance", "Rank B", null, "黒秘技・機々一発", "Kurohigi: Kiki Ippatsu", "Técnica Preta Secreta: Maquinário de Tiro Único", "Movimento Secreto Negro: Dama de Ferro", "Técnica Negra das Fincadas", "Técnica Secreta Negra: Mulher de Ferro: Golpe Fatal");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Salamandra", "Kankurō esconde Salamandra sob a terra, e, quando o adversário se posiciona na localização da marionete, o usuário a controla para capturar o adversário para dentro.", "Curto alcance", null, null, "黒秘技・山椒魚", "Kurohigi: Sanshōuo", "Técnica Secreta Preta: Salamandra", null, "Técnica Negra da Salamandra", "Movimento Secreto Negro: Salamandra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Preta Secreta: Três Grandes Desastres", "Kankurō usa essa técnica com Karasu, Kuroari e Sanshōuo. Primeiro as marionetes executam uma combinação de ataques contra o oponente, lançando-o no ar e ferindo-o brutalmente diversas vezes, com e sem lâminas. Quando o oponente está quase inconsciente, ele cai em frente da linha de ataque de Sanshōuo, que abre a boca e revela um canhão de chamas, explodindo o oponente no ar.", "Todos os alcances", null, null, "黒秘技・三大悲劇", "Kurohigi: Sandai Higeki", "Técnica Preta Secreta: Três Grandes Desastres", "Movimento Secreto Preto: Três Grandes Tragédias", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Proibida: Destruição do Selo do Mundo Impuro", "Orochimaru invoca um caixão como é utilizado na Invocação: Reencarnação do Mundo Impuro. Várias mãos fantasmagóricas saem do caixão, e em seguida, se estende através do corpo do oponente, danificando-o, até que uma mão maior agarra-o, e o esmaga, logo em seguida, puxa-o para dentro do caixão. A tampa do caixão se fecha e começa a afundar na escuridão que o rodeia. Orochimaru por fim, ri sinistramente, observando como ele desaparece.", "Todos os alcances", null, null, "禁術・穢土封滅きんじゅつ・えどふうめつ", "Kinjutsu: Edo Fūmetsu", "Técnica Proibida: Destruição do Selo do Mundo Impuro", "Jutsu Proibido: Destruição do Selo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Proibida: Golem de Lama", "Dōshin pode fazer duplicatas de seus oponentes a partir da Liberação de Terra. Tais duplicatas, além de adquirir as características do oponente, também adquirem suas técnicas.", "Todos os alcances", null, "Passaro", "禁術・泥人形", "Kinjutsu: Doro Ningyō", "Técnica Proibida: Golem de Lama", "Jutsu Proibido: Golem de Lama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Proibida: Selo Gedō", "É um kinjutsu desenvolvido por Orochimaru durante a Segunda Guerra Mundial Shinobi. Ele combina os dois poderes de selamento e confusão em uma técnica, dando-lhes os nomes de 'Selo Gedō: Selar' (外道ノ印・封, Gedō no In: Fū; Literalmente significa 'Selo do Caminho Exterior: Selar'; Games em Português 'Marca Gedō: Selar') e 'Selo Gedō: Confusão' (外道ノ印・乱, Gedō no In: Ran; Literalmente significa 'Selo do Caminho Exterior: Confusão'; Games em Português 'Marca Gedō: Confusão') ao usá-los separadamente.", "Curto alcance", null, "Selar: Rato → Hibrido de Rato e Selo de Confronto → Cao → Cavalo → Cobra → Macaco → Cobra,Confusao: Selo da Técnica da Morte Mútua das Cobras Gêmeas → Hibrido de Rato e Selo de Confronto → Cão → Cavalo → Rato → Sho → Coelho Espelhado → Selo da Técnica da Morte Mútua das Cobras Gêmeas → Hibrido de Rato e Selo de Confronto → Selo de Ativacão da Técnica de Troca da Mente do Corpo", "禁術・外道の印きんじゅつ・げどうのいん", "Kinjutsu: Gedō no In", "Técnica Proibida: Selo do Caminho Exterior", "Jutsu Proibido: Marca Gedō", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Quimera", "O Kimera no Jutsu é um kinjutsu que foi desenvolvido por Hiruko, um Nukenin de Konohagakure, a fim de compensar sua falta de habilidades especiais naturais.", "Curto alcance", null, "Boi → Cavalo → Lebre", "鬼芽羅の術キメラのじゅつ", "Kimera no Jutsu", "Técnica de Rebento Demoníaco de Rede de Captura", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Reversível", "Depois de invocar uma de suas cobras gigantes para engolir completamente o alvo, Orochimaru faz a cobra derramar completamente a sua pele. Em seguida, Orochimaru ordena a cobra digerir instantaneamente seu alvo.", "Curto alcance", null, null, "リバーシブルの術", "Ribāshiburu no Jutsu", "Técnica Reversível", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Saltitante Saltitante", "Esta é uma variação da Técnica Sensual. Usando as habilidades que Naruto lhes havia ensinado, Konohamaru, Moegi e Udon transformam-se em três lindas mulheres: Konohamaru vai se transformar em a noite na cidade, 'Pudim de Creme' (カスタードプリン, Kasutādo Purin; Ocidente: 'Modelo'); Moegi em a ida à praia, 'Pudim Queimado' (直火焼きプリン, Jikabiyaki Purin; Ocidente: 'Estrela de Cinema'); Udon em enfermeira, 'Pudim de Leite' (牛乳プリン, Gyūnyū Purin; Ocidente: 'Recepcionista do Salão de Bronzeamento'). Todos os três, então, saltam os seios para divertir os seus adversários, e por isso, os três foram capazes de fazer suas vítimas caírem inconscientes. Naruto sentiu que a técnica ainda precisava de 'bon-kyu-bon' (japonês para 'grande-pequeno-grande', referindo-se ás 'três medidas ideais', 'busto-cintura-quadril'), que foi censurada na versão Ocidental em 'Va Va Voom'.", null, null, null, "プリンプリンの術", "Purin Purin no Jutsu", "Técnica Pudim Pudim", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta Improvisada: Técnica de Shuriken nas Esferas de Relâmpago", "Trata-se de uma técnica colaborativa entre Boruto e Sarada para criar uma série de esferas de relâmpago anexadas às pontas de uma shuriken infundida por relâmpago e, enfim, serem lançadas. Por causa da carga elétrica altíssima, é possível ver o rastro da técnica, bem como causar uma explosão a qualquer contato.", "Médio alcance", null, null, "即席奥義・雷球手裏剣の術そくせきおうぎ・らいきゅうしゅりけんのじゅつ", "Sokuseki Ōgi: Raikyū Shuriken no Jutsu", "Técnica Secreta Improvisada: Técnica de Shuriken nas Esferas de Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Braço de Inseto", "Shino envia seus insetos que se ligam e formam um braço, que serve para capturar ou imobilizar.", null, null, null, "秘術・蟲腕ひじゅつ・むしうで", "Hijutsu: Mushiude", "Técnica Secreta: Braço de Inseto", "Jutsu Oculto: Braço de Inseto", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Casulo de Insetos", "Esta técnica exclusiva permite que o usuário acelere o crescimento de seus insetos. Como o corpo do usuário já é uma colmeia da sorte para os insetos, o usuário envolve-se em um casulo real para ajudar ainda mais os insetos. Este é um movimento extremamente útil para os membros do clã Aburame, já que seus ataques centram-se principalmente sobre o uso de insetos. No entanto, o uso desta técnica no meio de uma batalha pode ser potencialmente perigoso, pois deixa o usuário estático e vulnerável. Como tal, quando esta técnica está sendo empregada, o usuário geralmente encontra um local seguro ou de outra forma pode ser guardada por seu companheiro de equipe.", null, null, "Boi → Cao → Passaro → Carneiro", "秘術・蟲繭", "Hijutsu: Mushimayu", "Técnica Secreta: Casulo de Insetos", null, null, "Jutsu Oculto: Casulo de Insetos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Coleta de Insetos", "Com um simples toque em uma superfície com a palma e os dedos, o usuário libera uma pequena teia de chakra que atrai insetos para esse local. Uma vez que o Aburame pode se comunicar com os insetos, pode ser útil para ganhar informação através da recolha de insetos locais.", "Curto alcance", null, "Carneiro", "秘術・蟲寄せ", "Hijutsu: Mushiyose", "Técnica Secreta: Coleta de Insetos", null, null, "Jutsu de Invocação de Insetos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Esfera de Insetos", "Esta técnica cobre todo o corpo do inimigo com os kikaichū viventes dentro do corpo do membro do clã Aburame. Os kikaichū de sensoriamento de chakra são espalhados por uma vasta gama e no momento em que localizam o inimigo, os insetos, seguindo as ordens do usuário, reúnem-se de uma só vez. Os insetos cercam completamente o alvo, fazendo um globo. Eles contém o inimigo e começam a comer o seu chakra picada por picada. Em pouco tempo, a morte vai chegar. Mesmo se o alvo conseguir escapar, os insetos vão segui-lo imediatamente.", "Todos os alcances", "Rank –", null, "秘術・蟲玉ひじゅつ・むしだま", "Hijutsu: Mushidama", "Técnica Secreta: Esfera de Insetos", null, "Jutsu Secreto da Esfera de Insetos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Garoa", "Esta técnica é conhecida pelo uso exclusivo dos ninjas-caçadores de Kirigakure. O usuário cria uma potente chuva com gotas d'água capazes de absorver chakra. Tanto o Ninjutsu de Bolha de Sabão de Utakata como o Rasengan de Naruto foram absorvidos por esta técnica.", "Todos os alcances", null, "Carneiro → Cao → Rato", "秘術・霧雨ひじゅつ・きりさめ", "Hijutsu: Kirisame", "Técnica Secreta: Garoa", null, null, "Jutsu Secreto: Kirisame");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Invocação: Castelo Crepúsculo", "Kabuto usa oito etiquetas de selamento para invocar o Castelo Crepúsculo. O castelo pode absorver a vitalidade de vários alvos, capaz de aprisionar-los como fantasmas que vagam pelo castelo após terem toda sua vitalidade absorvida, alem de destruir todo o local a sua volta. Quando o castelo é destruído através da remoção das oito etiquetas de selamento, ele desaparece sem deixar vestígios.", "Todos os alcances", null, null, "秘術・口寄せ・無幻城ひじゅつ・くちよせ・むげんじょう", "Hijutsu: Kuchiyose: Mugenjō", "Técnica Secreta: Invocação: Castelo de Fantasmas Infinitos", "Jutsu Secreto: Invocação: Mugenjo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Liberação de Selamento", "Tsukino usa suas dois tantō para executar a sua técnica. Ao usar essa técnica, ela pode dissipar quaisquer selos.", null, null, null, null, null, "Técnica Secreta: Liberação de Selamento", "Jutsu Hiden: Destruição de Selamento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Pântano de Insetos", "Shino envia seus insetos e eles rapidamente envolvem o corpo do oponente. Em seguida, eles esmagam-no com força bruta.", "Curto a Médio alcance", null, null, "秘術・蟲沼", "Hijutsu: Mushinuma", "Técnica Secreta: Pântano de Insetos", "Jutsu Oculto: Pântano de Insetos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Tornado de Insetos", "Esta técnica cobre todo o corpo do inimigo com os kikaichū que vivem dentro do corpo do membro do clã Aburame. Os kikaichū sensores de chakra são espalhados por uma vasta gama, e no momento em que localizam o inimigo, os insetos, seguindo ordens do usuário, reúnem de uma só vez completamente em torno do alvo criando um tornado em movimento rápido que aparentemente desintegra o alvo.", "Todos os alcances", null, null, "秘術・蟲竜巻ひじゅつ・むしたつまき", "Hijutsu: Mushitatsumaki", "Técnica Secreta: Tornado de Insetos", "Insetos Parasitas: Tufão[1]", null, "Jutsu Oculto: Tornado de Besouros");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta: Técnica do Selo de Boca", "Quando essa técnica é utilizada, isso faz com que o alvo se torne mudo até que a técnica seja dissipada pelo utilizador. O usuário, então, realiza o selo específico da técnica e, aparentemente, como parte do ritual necessário para a técnica, uma pergunta é feita para o alvo. Com a resposta do oponente, a técnica é então imediatamente invocada fazendo com que a pessoa perca o uso de sua voz.", null, null, "Selo especifico da técnica", "秘伝術・口の印の術", "Hiden Jutsu: Kuchi no In no Jutsu", "Técnica Secreta: Técnica do Selo de Boca", null, null, "Secreto: Jutsu do Selo Bocal");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta da Liberação de Madeira: Natividade de um Mundo de Árvores", "É uma técnica desenvolvida por Hashirama Senju, o fundador de Konohagakure, em que usuário obriga árvores a crescer em qualquer superfície, facilmente criando uma floresta densa em qualquer lugar que escolher. Uma pequena planta pode crescer em uma floresta em um instante. Gerando chakra, o usuário manobra ela como entender para ataque e defesa; e além disso, esta técnica onipotente, ainda permite capturar o inimigo ao mesmo tempo. Com sua força de vida enorme, as árvores podem perfurar paredes de barro, e estender seus ramos em suas presas em um instante. Mesmo afirmando que Konohagakure não teria sido estabelecida sem esta técnica não seria um exagero.", "Todos os alcances", null, "Tigre → Cobra ou Carneiro → Cobra[2]", "木遁秘術・樹界降誕もくとんひじゅつ・じゅかいこうたん", "Mokuton Hijutsu: Jukai Kōtan", "Técnica Secreta da Liberação de Madeira: Natividade de um Mundo de Árvores", "Jutsu Secreto do Estilo Madeira: Surgimento da Floresta Profunda", "Jutsu Secreto do Estilo da Madeira: Nascimento da Floresta", "Estilo Terra: Imersão da Floresta Profunda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Secreta das Agulhas de Pedra", "Esta técnica faz uso do chakra como um agente paralisante. O usuário lança uma kunai com fios de aço contra o inimigo. Uma vez atingido, o chakra corre através do fio para paralisá-lo, impedindo de atacar devido à paralisia. No entanto, quando usado contra usuários de absorção de chakra, esta técnica pode ser contornada, tendo o chakra roubado, assim como Kisame Hoshigaki fizera.", "Curto alcance", null, null, "秘術石針ひじゅついしばり", "Hijutsu Ishibari", "Técnica Secreta das Agulhas de Rocha", null, "Técnica Secreta Agulha de Pedra", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Sensorial", "A Técnica Sensorial, usada pelos shinobi tipo sensor, permite ao usuário detectar chakra. Usando esta técnica, os sensores podem facilmente detectar e rastrear alvos. O alcance desta técnica varia entre os sensores.", "Todos os alcances", null, null, "感知の術かんちのじゅつ", "Kanchi no Jutsu", "Técnica Sensorial", null, null, "Jutsu Sensorial");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Sensual", "A Técnica Sensual é simplesmente uma Técnica de Transformação usada para transformar o usuário em uma mulher bonita, nua de uma forma muito sedutora com pose, névoas de fumaça cobrindo suas partes íntimas, ou usando um maiô. Naruto usa essa técnica para distrair ou conquistar os homens, às vezes levando a um sangramento exagerado por parte da vítima ou até mesmo inconsciência, enquanto Konohamaru usa principalmente para impressionar Naruto.", null, "Rank E", "Bode", "おいろけの術おいろけのじゅつ", "Oiroke no Jutsu", "Técnica Sensual", null, null, "Jutsu Sexy");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Sensual: Pole Dance e Corpo Agradável", "Nesta outra variação da Técnica Sensual, o usuário e um bastão são transformados em uma mulher com um corpo especialmente atraente (daí o 'Nice Body' do título) em um biquíni vermelho quente e um pole dance. A mulher pisca, sopra beijos e fala sedutoramente com a vítima para adicionar à eficácia desta técnica. Konohamaru mostrou esta técnica para Naruto em uma tentativa fracassada de impressioná-lo, apenas para descobrir que Naruto já havia aprendido durante o desenvolvimento da Nova Técnica Ninja Pervertida.", null, null, null, "おいろけの術・ポールダンス・アンド・ナイス・ボディー", "Oiroke no Jutsu: Pōrudansuando Naisu Bodī", "Técnica Sensual: Pole Dance e Corpo Agradável", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Sensual das Quatro Adultas", "Boruto cria três clones das sombras e os quatro se transformam em Sakura Haruno, Ino Yamanaka, Tenten e Karui com trajes de banho, a fim de distrair o adversário.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Sensual do Harém Invertido", "Uma variação da Técnica do Harém, esta técnica utiliza vários clones das sombras que Naruto transforma em homens nus. Esta técnica se mostrou ser eficiente para distrair temporariamente um oponente feminino, por tempo suficiente para que Naruto possa realizar alguma ação posterior, tais como acertar um ataque surpresa no mesmo.", null, "Rank D", null, "おいろけ逆ハーレムの術", "Oiroke Gyaku Hāremu no Jutsu", "Técnica Sensual do Harém Invertido", "Jutsu Sensual do Harém Invertido", "Técnica Sexy do Harém Reverso", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Sexagenária do Estilo Hokage: Kakuan Entrando na Sociedade com Mãos Trazendo Felicidade", "Esta técnica utiliza o poder da Liberação de Madeira para suprimir um jinchūriki a parar de usar o chakra das bestas com caudas. Para invocar esta técnica, é necessário que o usuário ou um jinchūriki esteja com a Jóia de Cristal que responde ao chakra do Primeiro Hokage. O usuário produz o kanji 'sentar' (座, za) em sua palma, e tocando a besta com cauda, ou seu jinchūriki, com a mão, o usuário suprime o chakra dentro de uma área cercada com dez pilares.", "Curto alcance", "Rank –", "Javali → Cao → Passaro → Macaco → Carneiro → Macaco → Tigre", "火影式耳順術　廓庵入鄽垂手ほかげしきじじゅんじゅつ　かくあんにってんすいしゅ", "Hokage-Shiki Jijun Jutsu — Kakuan Nitten Suishu", "Técnica Sexagenária do Estilo Hokage: Kakuan Entrando na Sociedade com Mãos Trazendo Felicidade", null, null, "Estilo Hokage Jutsu do Sabugueiro: Décima Ordem de Iluminação");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Vermelha Secreta: Maquinário Triangular", "Usando sua marionete Sasori, Kankurō desanexa a cabeça e lança várias lâminas triangulares com esferas com explosivos ligadas a elas a partir do pescoço e das palmas das mãos da marionete.", "Médio a Longo alcance", null, null, "赤秘技・機々三角", "Akahigi: Kiki Sankaku", "Técnica Vermelha Secreta: Maquinário Triangular", null, "Técnica Vermelha do Triângulo Mecânico", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Vermelha Secreta: Performance das Cem Marionetes", "Inicialmente, Sasori remove um pergaminho de invocação de suas costas. Ele então abre seu compartimento no peito direito emitindo mais de uma centena de cordas chakra, que atingem o pergaminho. Desde o pergaminho, mais de cem marionetes vestindo mantos vermelhos emergem.", "Todos os alcances", "Rank S", null, "赤秘技・百機の操演", "Akahigi: Hyakki no Sōen", "Técnica Secreta Vermelha: Performance das Cem Marionetes", null, null, "Ataque Vermelho Secreto: Atuação de Cem Marionetes");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Abelha do Mel", "O usuário convoca um grande número de abelhas que enxameiam e picam o adversário. Toda vez que uma abelha é ferida ou destruída, libera o mel pegajoso sobre o adversário que é forte o suficiente para imobilizá-lo. Isto é particularmente eficaz contra adversários que contam com ataques físicos curto alcance, como os praticantes de Punho Gentil.", "Todos os alcances", null, null, "蜂蜜の術", "Hachimitsu no Jutsu", "Técnica da Abelha do Mel", null, null, "Arte Ninja: Jutsu Mel de Abelha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Alma Morta", "Por meio desta técnica, Kabuto invoca um cadáver e o reanima temporariamente, a fim de controlar o corpo a partir de seu chakra. O cadáver pode ser usado como distração ou como aliado, inclusive com acesso às suas técnicas de origem, embora de forma limitada aos corpos originais, uma vez que os cadáveres falsificados somente absorvem as características físicas.", "Curto a Médio alcance", "Rank B", null, "死魂の術しこんのじゅつ", "Shikon no Jutsu", "Técnica da Alma Morta", null, null, "Jutsu da Alma Morta");
