INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Aranha de Vento", "Jigumo pode invocar suas aranha de vento, gerando trilhas, que podem ser usadas para disparar em seu adversários com teias em altas velocidades (como mostra a Marionetes Acidentadas), os adversários podem cercá-los dentro de um quarto cheio de aranhas e teias, ou fazer um moinho de vento de teias. Esta técnica foi utilizada para rastrear o time que estava com Jiraiya rondando por uma trilha prateada. Isso pode ser útil com uma técnica de ventriloquismo.", "Todos os alcances", null, null, "風蜘蛛の術", "Kazegumo no Jutsu", "Técnica das Aranhas de Vento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Areia Manipuladora de Memória-Oculta", "O usuário sela de longe as memórias do alvo, enterrando uma agulha extremamente pequena no centro de memória do cérebro. As memórias podem ser restauradas cancelando a técnica, destruindo assim a agulha. Se um subordinado tivesse essa operação realizada em seu cérebro e se infiltrasse no campo inimigo, ele pode executar com segurança a atividade de espionagem durante um longo período de tempo sem nenhuma memória do procedimento. Se o usuário cancela a técnica, o alvo mais uma vez lembra de sua missão como agente adormecido.", "Todos os alcances", "Rank A", null, "潜脳操砂の術", "Sennō Sōsa no Jutsu", "Técnica da Areia Manipuladora de Memória-Oculta", null, null, "Jutsu da Lavagem Cerebral");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Armadilha da Rocha Oculta", "Esta armadilha estava entre as muitas que protegiam o Monte Katsuragi. Após atravessar o local onde a armadilha foi colocada, um cilindro de rocha aparece e empurra o inimigo para fora da montanha.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Armadilha de Chakra nas Pétalas", "Ao lançar pétalas de flores, Ino infunde as pétalas das flores com o seu chakra para prender qualquer um que dê passos sobre elas. Após a imobilização, os oponentes se tornam um alvo fácil para a seus ataques posteriores.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Armadilha de Chakra no Cabelo", "Depois de cortar o cabelo, Ino infunde os fios de cabelo com o seu chakra para prender qualquer um que dê passos sobre eles. Após a imobilização, os oponentes se tornam um alvo fácil para a sua Técnica de Troca da Mente do Corpo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Auto-Cura", "É uma técnica regenerativa utilizada por Boro com o auxílio de uma ferramenta ninja científica acoplada em seu corpo.", null, null, "Passaro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Barreira de Bolha", "Utakata usa seu Ninjutsu de Bolha de Sabão para criar um domo em torno de si mesmo e de seus aliados. Esta defesa é mostrada sendo forte o suficiente para resistir a grandes explosões.", "Curto alcance", null, null, "結界しゃぼん玉の術けっかいしゃぼんたまのじゅつ", "Kekkai Shabontama no Jutsu", "Técnica da Barreira de Bolha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Barreira de Cristal", "Malice cria uma barreira de cristal. Tudo dentro dos limites da barreira é mantido em animação suspensa e é necessário chakra constante para manter a vítima na barreira de cristal. Se o usuário estiver de alguma forma enfraquecido e os alvos que foram selados ainda tiverem chakra o suficiente, eles conseguem escapar.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Barreira de Isolamento", "Trata-se de um fūinjutsu de barreira utilizado pelo Time de Selamento das Forças Aliadas Shinobi.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Barreira de Névoa Carmesim", "Está técnica permite convocar uma parede densa de névoa, podendo prejudicar a visão para todos dentro dela. Como também disfarça qualquer som que seja ouvido fora da névoa. A eficácia desta técnica varia entre os usuários, pois depende da habilidade do usuário.", "Curto a Médio alcance", null, "Cao → Serpente → Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Barreira do Sino", "Esta é uma técnica em que o Sino de Segurança Místico de Shion projeta um holograma em torno do indivíduo que está segurando o sino, interceptando qualquer dano que possa constituir uma ameaça para eles. Miroku diz que nada poderia destruí-lo, e, com suas propriedades, Shion seria totalmente protegida. Ela ainda tem o poder de explodir ameaças que encostam, como ela fez com o corpo de Mōryō.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Bomba de Abelha", "Esta técnica consiste em abelhas com papéis explosivos que são ligados à elas. Uma vez que as abelhas entram em contato com o seu alvo, os selos se explodem.", "Médio a Longo alcance", null, null, "蜂爆弾の術", "Hachi Bakudan no Jutsu", "Técnica da Bomba de Abelha", null, null, "Jutsu da Bomba de Abelha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Capa da Invisibilidade", "Esta técnica permite que um ninja possa fazer aparecer um pano ou um pedaço de pano e mistura em um objeto, tornando-os invisíveis. Konohamaru usa esta técnica constantemente no início da série para tentar enganar Naruto Uzumaki, mas usa disfarces baratos, como a rocha quadrada com os buracos para os olhos, capas com estampas incorretas ou girada em torno da maneira errada, ou simplesmente não cobrindo-se completamente, dessa forma desfazendo a ilusão.", null, "Rank E", null, "隠れ蓑の術かくれみののじゅつ", "Kakuremino no Jutsu", "Técnica da Capa da Invisibilidade", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Captura pela Sombra", null, "Curto a Médio alcance", "Rank A", null, "影掴みの術かげずかみのじゅつ", "Kagezukami no Jutsu", "Técnica da Captura pela Sombra", null, "Técnica de Captura de Sombras", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Casca Vazia de Cigarra", "Ao projetar sua voz através de varias direções, o usuário é capaz de mascarar sua verdadeira localização, bem como dos que estão com ele, para confundir o adversário. Mesmo ninjas do tipo sensor podem ser facilmente enganados por essa técnica..", "Todos os alcances", null, "Carneiro", "空蝉の術", "Utsusemi no Jutsu", "Técnica da Casca Vazia de Cigarra", null, null, "Jutsu da Ventriloquia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Chegada da Fênix com Incêndio Elétrico", "Uma técnica de colaboração usada entre um usuário de Liberação de Relâmpago e outro de Liberação de Fogo. Usando do relâmpago, o inimigo é eletrocutado, antes de ser, logo após, carbonizado com o fogo expelido. O contato do relâmpago com o fogo gera uma explosão.", "Curto a Médio alcance", null, null, "雷火鳳来の術らいかほうらいのじゅつ", "Raika Hōrai no Jutsu", "Técnica da Chegada da Fênix com Incêndio Elétrico", "Jutsu do Trovão de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Costura pelas Sombras", "Diferente da Técnica de Imitação pela Sombra, esta técnica permite ao usuário capturar, imobilizar e, ainda, atacar algum alvo. Por ser bastante abrangente, não requer do usuário a concentração para estender sua sombra, pelo contrário o usuário pode materializar a sombra para alcançar o que a sombra na superfície normalmente não alcançaria. Com esta técnica, puxar objetos muito pequenos se torna relativamente fácil.", "Curto a Médio alcance", "Rank –", "Rato → Passaro", "影縫いの術かげぬいのじゅつ", "Kagenui no Jutsu", "Técnica da Costura pelas Sombras", "Arte Ninja: Costura da Sombra[2]", "Técnica da Costura das Sombras", "Arte Ninja: Jutsu da Costura da Sombra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Criação de Todas as Coisas", "A Técnica da Criação de Todas as Coisas foi a habilidade possuída por Hagoromo Ōtsutsuki, que usou isso para criar as nove bestas com cauda. O processo usado é explicado inicialmente envolvendo a administração de imaginação, e a energia espiritual que constitui a base do chakra Yin para criar formas físicas do nada. Então, através da aplicação de vitalidade e energia física que forma a base do chakra Yang, ele iria dar vida às criações resultantes.", null, null, null, "万物創造の術", "Banbutsu Sōzō no Jutsu", "Criação de Todas as Coisas", null, "Criação Universal", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Cura Mística", "O usuário é capaz emanar o seu chakra na forma de uma barreira que cura seus aliados que estiverem dentro do seu alcance.", "Curto a Médio alcance", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Cópia Facial Extinguível", "Esta técnica foi desenvolvida para espionagem, porém possuí efeitos terríveis. O usuário coloca a mão no rosto da vítima, roubando suas características e tornando-as suas. Pois o que é roubado é o próprio rosto, o disfarce é conseguido através da alteração facial característica da vítima e não revela o menor vestígio do disfarce. As vítimas desta técnica são reduzidas a cadáveres sem rosto. O usuário pode até mesmo mudar a sua voz para soar como a de suas vítimas, fazendo uma cópia muito convincente. No entanto, com bastante força, o rosto pode enrugar e desgastar a ponto do rosto real do usuário aparecer por debaixo do disfarce. O rosto pode ser retirado a qualquer momento.", "Curto alcance", "Rank B", null, "消写顔の術", "Shōshagan no Jutsu", "Técnica da Cópia Facial Extinguível", null, null, "Técnica da Cópia de Rosto que Desaparece");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Dança do Tubarão na Prisão de Água", "Depois de usar a Liberação de Água: Grande Onda de Choque Explosiva de Água e se fundir com Samehada, Kisame Hoshigaki pode controlar todas as coisas dentro da água, e, considerando que esta técnica altera a sua posição, a fim de refletir a direção de seus movimentos, é difícil para o inimigo escapar enquanto Kisame o persegue. À esta técnica, requer respiração debaixo d'água e controle do próprio corpo — capacidades básicas de Kisame enquanto em sua forma fundida —, invalidando qualquer movimento do inimigo. Devido a isso, a morte é quase inevitável, tendo como desvantagem apenas o tempo de perseguição para Kisame, de acordo com sua quantidade de alvos.", "Todos os alcances", null, null, "水牢鮫踊りの術すいろうさめおどりのじゅつ", "Suirō Sameodori no Jutsu", "Técnica da Dança do Tubarão na Prisão de Água", null, null, "Jutsu da Prisão de Água do Tubarão Dançante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Desilusão do Inseto", "O usuário envia seus insetos abaixo do oponente antes de prender-se nele, alimentando-se de seu chakra. Essa técnica é poderosa o suficiente para derrubar mesmo pessoas que absorvem chakra, porém não é muito efetiva se o oponente tem reservas de chakra que ultrapassa os kikaichū que tem a capacidade de se alimentar do chakra.", "Curto a Médio alcance", null, "Tigre → Selo especifico da técnica", "蟲騙しの術むしだましのじゅつ", "Mushi Damashi no Jutsu", "Técnica da Desilusão do Inseto", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Divisão do Terreno", "Depois de executar os selos de mão necessários, o usuário bate as mãos no chão, o que faz com que a terra entre em erupção debaixo dos pés de seus oponentes. Essa técnica é efetiva ao criar barricadas que levam algum tempo para uma pessoa normal transpor.", "Curto a Médio alcance", null, "Coelho → Javali → Rato → Cavalo → Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Entrada Oculta", "O usuário estende sua palma da mão em direção a uma parede, causando um buraco com ondulações e em forma de uma porta. Além disso, um selo pode ser colocado na parede para proteger o conteúdo por trás dela.", "Curto alcance", null, "Terra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Espada Explosiva: Freio Repetido da Morte Explosiva", "Jogando um largo rolo de papéis explosivos da Shibuki, Jinpachi envia-a para seu inimigo em um movimento circular, cortando qualquer rota de fuga e engolindo-o em uma explosão generalizada.", "Todos os alcances", null, null, "爆刀術・発破勒重死", "Bakutō Jutsu: Happa Rokujūshi", "Técnica da Espada Explosiva: Freio Repetido da Morte Explosiva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Espada Sombria", "Após performar o selos de mão necessários, o usuário mantém o selo de confronto em uma das mãos e submerge a outra em sua própria sombra, de onde tira uma potente espada criada ao dar forma à sombra imbuída com um fluxo de chakra escuro. A técnica é poderosa o suficiente para causar dano ao ambiente quando acertado, bem como resistente o suficiente para resistir a uma fonte luz potente e direta, mas uma vez que o usuário perde o controle, ela desaparece.", "Curto alcance", null, "Selo especifico da técnica → Selo de Confronto", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Espada de Gelo", "O usuário condensa a umidade na atmosfera em espadas de gelo que podem então ser lançadas em um alvo. As espadas podem ser formadas a partir de grandes distâncias.", "Curto a Médio alcance", null, null, "氷剣の術ひょうけんのじゅつ", "Hyōken no Jutsu", "Técnica da Espada de Gelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Foice da Doninha", null, "Curto a Médio alcance", "Rank C", "Cobra → Dragao → Passaro[4]", "カマイタチの術", "Kamaitachi no Jutsu", "Técnica da Foice da Doninha", "Jutsu do Redemoinho[2]", "Arte Ninja da Técnica das Lâminas de Vento", "Jutsu Foice de Vento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Foice de Névoa", "Hanzō convoca Ibuse para atirar o oponente no ar e expele uma névoa venenosa, onde ele corta a nuvem de veneno em torno do oponente.", "Todos os Alcances", null, null, "鎌霧の術", "Kamakiri no Jutsu", "Técnica da Foice de Névoa", "Arte Ninja: Técnica da Foice do Nevoeiro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Formação do Trovão Voador", "Foi uma técnica de locomoção ensinada apenas para o Pelotão de Guardas do Hokage pelo Quarto Hokage, para melhorar os seus deveres de proteger o Hokage em exercício. Todos os três membros da equipe são necessários para execução desta técnica. Para realizar a técnica, eles formam um círculo com cada um formando com o outro o Selo do Confronto de forma com que os seus dedos polegares, médios e indicadores toquem nos dos outros. Isto os permite a teletransportar a si mesmos e qualquer coisa ou pessoa dentro do círculo para um local marcado. Desde de que se tornaram os guardas da Quinta Hokage, eles a marcaram com a fórmula da técnica para que eles pudessem se teletransportar para o seu lado a qualquer momento.", "Todos os alcances", null, "Selo do Confronto", "飛雷陣の術", "Hiraijin no Jutsu", "Técnica da Formação do Trovão Voador", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Fuga de Cordas", "Uma técnica básica ensinada na Academia, que permite que um ninja se liberte de cordas. Não se sabe como isto é feito ao certo.", null, "Rank E", null, "縄抜けの術", "Nawanuke no Jutsu", "Técnica da Fuga de Cordas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Grande Foice da Doninha", "Esta técnica é uma versão mais poderosa da Técnica da Foice da Doninha em que muitas correntes de ar se chocam para criar bolsas de vácuo que cortam os adversários, com potência suficiente para cortar e para fatiar muitas árvores em uma floresta. Esta técnica pode desviar ataques de ondas físicas e de ataques de som, ao mesmo tempo, tornando-a uma técnica ofensiva e defensiva.", "Todos os alcances", "Rank B", null, "大カマイタチの術", "Daikamaitachi no Jutsu", "Técnica da Grande Foice da Doninha", "Foice de Vento: Florescimento Máximo do Vento", "Técnica das Grandes Lâminas de Vento", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Grande Planície de Campo Minado", "Tobi entra no subsolo e circunda o adversário com muitas minas. Enquanto ele está cavando para sair, ele fica preso perto da explosão, resultando em ele ser derrubado com fogo sobre a sua capa. Embora Tobi foi pego na explosão, nenhum dano é infligido sobre ele como resultado deste ataque.", "Curto alcance", null, null, "地雷大平原の術", "Jirai Daiheigen no Jutsu", "Técnica da Grande Planície de Campo Minado", "Jutsu da Grande Planície do Campo Minado", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Invocação Amplificada", "Esta é uma técnica de invocação com condições especiais. A criatura invocada vem com a característica de se multiplicar quando recebe danos, e sempre que essa condição é cumprida, a própria criatura ativa o efeito de multiplicação.", "Curto alcance", "Rank B", null, "増幅口寄せの術ぞうふくくちよせのじゅつ", "Zōfuku Kuchiyose no Jutsu", "Técnica da Invocação Amplificada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Juba do Leão Selvagem", null, "Todos os alcances", "Rank B", "Serpente", "乱獅子髪の術らんじしがみのじゅつ", "Ranjishigami no Jutsu", "Técnica da Juba do Leão Selvagem", null, "Jutsu da Juba Rebelde de Leão", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Lança Relâmpago", "É uma técnica de Liberação de Relâmpago desenvolvida por Boruto Uzumaki.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Liberação do Selamento", "Depois de jogar o rosário na vítima selada, Chūkaku começa a murmurar palavras e realizar movimentos de mão ritualísticos, levando a fórmula do selo desaparecer lentamente. Esta técnica parece usar muita concentração e força, pois Chūkaku parecia perturbado durante a execução deste.", "Curto alcance", null, "Selo de Confronto em Cada Mao", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Ligação de Pano", "O usuário utiliza um gigante rolo de pano para embrulhar bem o adversário, e imobilizando-o totalmente, evitando assim que ele se mova ou use qualquer técnica. A capacidade em si é versátil já que uma vez concluída, a adição dos selos pode ser feita no topo das vítimas, impedindo-as de até mesmo serem invocadas. Esta técnica parece ser comum entre alguns ninja de Sunagakure.", "Curto a Médio alcance", null, null, "布縛りの術ぬのしばりのじゅつ", "Nunoshibari no Jutsu", "Técnica de Ligação de Pano", null, "Técnica das Amarras de Pano", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Maldição da Marionete de Areia", "Com o auxílio de areia pré-existente, o usuário cria uma grande quantidade de marionetes conscientes de areia e as envia para lutar com o oponente. As marionetes, no entanto, não são fortes e podem ser facilmente derrotadas com um único golpe.", "Curto Alcance", null, "Selo especifico da técnica → Bater as maos no solo", "呪いの砂人形の術", "Noroi no Suna Ningyō no Jutsu", "Técnica da Maldição da Marionete de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Marionete do Selo Amaldiçoado de Transferência de Mente", "O usuário desta técnica em primeiro lugar transfere sua mente em uma marionete colocada em algum lugar antes, perto da vizinhança do caminho previsto de um inimigo, antes de manipulá-lo para atacar um oponente que se destina. Quando o inimigo contra-ataca e inflige danos sobre a marionete ou a marionete inflige danos ao inimigo, o selo amaldiçoado terá efeito, fazendo com que o kanji 'maldição' (呪, noroi) desapareça da testa da marionete e as duas consciências (do usuário que está dentro da marionete e que a vítima rebateu) mudam. Consequentemente, o usuário vai ganhar controle completo do corpo do oponente, enquanto a mente da vítima permanece presa dentro da marionete imóvel.", "Longo alcance", "Rank –", null, "心転傀儡呪印の術", "Shinten Kugutsu Juin no Jutsu", "Técnica da Marionete do Selo Amaldiçoado de Transferência de Mente", null, "Técnica de Transferência da Mente Para um Boneco Amaldiçoado", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Mina Terrestre de Lança-ificação", "Uma técnica de colaboração realizada por usuários de Liberação de Terra e Liberação de Relâmpago. O usuário de Liberação de Terra usa seu chakra para criar uma lança feita de areia e terra, que é jogada ao chão. O usuário de Liberação de Relâmpago então eletrifica a lança, causando graves danos ao oponente.", "Longo alcance", null, null, "地雷槍化の術じらいそうかのじゅつ", "Jirai Sōka no Jutsu", "Técnica da Mina Terrestre de Lança-ificação", "Jutsu da Lança de Trovão da Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Morte Mútua das Cobras Gêmeas", "Essa é uma técnica proibida de aspectos suicidas, que mata ambos usuário e alvo. O usuário junta as suas mãos com a do inimigo, fazendo selos de forma compartilhada. Uma vez finalizados, duas serpentes saem das mangas do usuário, aplicando um veneno mortal através da mordida, tanto nele como no oponente. Quando Anko Mitarashi usou essa técnica contra Orochimaru, este se substituiu por um clone para evitar danos, impedindo Anko de finalizar a técnica e matá-lo.", "Curto alcance", "Rank A", null, "双蛇相殺の術", "Sōjasōsai no Jutsu", "Técnica da Morte Mútua das Cobras Gêmeas", "Jutsu das Cobras Gêmeas Assassinas", null, "Jutsu Sacrifício das Serpentes Gêmeas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Nevasca da Flor de Cerejeira", "Sakura joga numerosas kunai ligadas à sacos com pequenas marcas de explosivos e pétalas de flor de cerejeira para ajudar a camuflar as marcas. Sakura simplesmente lança uma kunai adicional com um papel explosivo, para inflamar as marcas e pegar o inimigo em uma grande explosão.", "Longo alcance", null, null, "サクラ吹雪の術", "Sakura Fubuki no Jutsu", "Técnica da Nevasca da Flor de Cerejeira", "Arte Ninja: Dança da Cerejeira de Neve", null, "Jutsu Nevasca Sakura");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Nuvem Tempestuosa", "Um genjutsu que convoca nuvens tempestuosas que cobrem o céu e desencadeiam vários raios, podendo atrapalhar os sentidos do oponente e paralisá-lo de medo. A técnica permanece ativa enquanto o usuário mantiver o último selo de mão e se dissipa quando ele é desfeito, mas alguns resquícios dela ainda permanecem aparentes por um certo tempo.", "Todos os alcances", null, "Boi → Javali → Rato", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Nuvem de Ovelha", "Shikamaru cria uma ovelha ilusória que coloca o inimigo para dormir.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Nuvem de Veneno", "Formando uma pequena esfera de seus Rinkaichū entre suas mãos, Torune pode explodi-los em uma cortina de fumaça em toda a área na frente dele. Dada a natureza de seus insetos, esta técnica é de curta duração se não for eficaz, a nuvem de insetos rapidamente se dispersa. No entanto, se tocado ou inalado é uma fatalidade garantida.", "Curto à Médio alcance", null, null, "毒塵の術どくじんのじゅつ", "Dokujin no Jutsu", "Técnica da Nuvem de Veneno", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Névoa Venenosa da Boca de Cobra", "Usando a sua cauda de cobra, Kabuto pulveriza uma nuvem venenosa sobre o oponente, o que provoca efeitos devastadores quando inalado pelo oponente.", "Todos os alcances", null, null, "蛇口毒霧の術じゃぐちどくりぎのじゅつ", "Jaguchi Dokugiri no Jutsu", "Técnica da Névoa Venenosa da Boca de Cobra", "Jutsu do Nevoeiro Peçonhento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Névoa de Tinta", "O usuário envolve seu corpo em chakra fundido de tinta para esconder-se e criar uma cortina de fumaça eficaz. Quando utilizado em combinação com a Técnica de Cintilação Corporal, o usuário pode rapidamente deixar o campo de batalha sem ser notado.", null, "Rank C", null, "墨霞の術", "Sumigasumi no Jutsu", "Técnica da Névoa de Tinta", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Onda de Som", "Uma técnica que permite ao usuário criar uma explosão de ondas sonoras que aumenta à sua volta. Esta técnica foi forte o suficiente para não só destruir o laboratório de Orochimaru, mas também subjugar Hiruzen Sarutobi, Enma, e vários Anbu que os acompanhavam.", "Curto a Médio alcance", null, "Tigre → Boi → Cao → Coelho → Cobra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Palma Mística", "Este ninjutsu médico permite ao usuário acelerar o processo de cura natural do corpo, enviando chakra de suas mãos em uma ferida ou aflições das partes do corpo. Isso permite que o utilizador cure um paciente sem a necessidade de equipamento médico ou cirúrgico, o que torna a técnica muito útil no campo de batalha. Ele pode ser usado para tratar ambos os ferimentos externos e internos. É vital para corresponder à quantidade de chakra utilizado para a gravidade da doença ou lesão. Isso requer uma grande quantidade de controle de chakra. Devido a isso, apenas alguns ninjas médicos altamente qualificados são capazes de usar esta técnica. Kabuto Yakushi demonstrou a capacidade de usá-la à uma curta distância do alvo, em vez do contato direto.", "Curto alcance", "Rank A", "Boi modificado → Tigre varia", "掌仙術しょうせんじゅつ", "Shōsen Jutsu", "Técnica da Palma Mística", "Ninjutsu Médico: Flash Feroz[2]", null, "Jutsu de Cura");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Passagem da Barreira de Cinco Selos", "Para entrar o limite criado pela Barreira de Cinco Selos sem dissipar a técnica, Deidara usou uma técnica que levantou a pedra desbloqueando a entrada para o esconderijo da Akatsuki, concedendo a ele e Sasori passarem para o esconderijo, mantendo a barreira intacta.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Performance de Dança da Mente", "O Shinra Enbu no Jutsu (心乱演舞の術, Dança Destrutiva de Corpo e Mente) é simplesmente uma versão em larga escala da Técnica de Perturbação da Mente do Corpo, pois a área onde o jutsu afeta as pessoas é muito maior. Como sua versão menor, a técnica permite que o usuário tome controle do corpo do alvo, muitas vezes obrigando aliados à atacarem uns aos outros.", "Curto à Médio alcance", null, null, "心乱演舞の術", "Shinran Enbu no Jutsu", "Dança Destrutiva de Corpo e Mente", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Prisão de Gelo", "Esta técnica permite ao usuário infundir seu chakra com gelo no chão e trazê-lo para a superfície. O usuário pode, então, prender o seu adversário dentro do gelo, controlando os seus movimentos e envolve-lo completamente no gelo. É forte o suficiente para suportar as técnicas básicas de Liberação de Fogo, porque o gelo já foi infundido com chakra do usuário. O usuário também pode cercar-se com a prisão de gelo para agir como um escudo.", "Curto a Médio alcance", null, null, "氷牢の術ひょうろうのじゅつ", "Hyōrō no Jutsu", "Técnica da Prisão de Gelo", "Liberação de Gelo: Prisão de Gelo", null, "Jutsu da Prisão de Gelo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Prisão de Água", "A Técnica da Prisão de Água é utilizada para capturar uma vítima dentro de uma esfera praticamente inevitável de água; com a água podendo ser retirada de fontes próximas ou até mesmo expelida pela boca. Uma vez preso o alvo é incapaz de se mover ou respirar enquanto dentro dela devido ao peso da água. Por causa da densidade da água, pode ser utilizada, de uma forma limitada, para fins defensivos, se executadas em si mesmo. Ela também pode ser armazenada em pergaminhos como armadilhas.", "Curto a Médio alcance", "Rank C", "Cobra → Carneiro → Cavalo → Coelho → Carneiro → Cavalo → Coelho → Estender a mao ou (Cobra → Boi → Juntar as Mãos)", "水牢の術", "Suirō no Jutsu", "Técnica da Prisão de Água", "Estilo Água: Jutsu Prisão de Água", null, "Jutsu Prisão de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Rede das Sombras", "Shikamaru estende sua sombra para controlar o oponente e também tomar posse da sombra dele. Feito isso, Shikamaru puxa as duas sombras, como uma rede, e, em seguida, laceia fortemente o oponente até a morte.", "Curto alcance", null, null, "影網の術かげもうのじゅつ", "Kagemō no Jutsu", "Técnica da Rede das Sombras", "Técnica da Contenção das Sombras", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Rotação Mútua Instantânea do Deus Voador do Trovão", null, "Curto alcance", "Rank A", null, "飛雷神互瞬回しの術ひらいしんごしゅんまわしのじゅつ", "Hiraishin Goshun Mawashi no Jutsu", "Técnica da Rotação Mútua Instantânea do Deus Voador do Trovão", null, "Técnica do Deus do Relâmpago Mútuo Instantâneo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Shuriken das Sombras", "É uma técnica simples onde duas shuriken, como a Fūma Shuriken, são empilhadas uma sobre a outra e lançadas simultaneamente. No entanto, dependendo da forma como é usado, ele pode provar a sua eficiência absoluta. O truque é de alguma forma chamar a atenção do inimigo para a shuriken superior. Então, eles têm que lidar com a shuriken que vem por debaixo da shuriken superior, despercebida. No entanto, se o inimigo perceber as duas shuriken, a técnica acaba por perder toda a sua eficiência.", "Médio a Longo alcance", "Rank D", null, "影手裏剣の術", "Kage Shuriken no Jutsu", "Técnica da Shuriken das Sombras", null, null, "Jutsu Shuriken das Sombras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Silhueta das Sombras", "Shikamaru materializa e personifica sua sombra em vários clones idênticos de sua pessoa ou de seu inimigo. As sombras, enquanto em um lugar com pouca iluminação, podem atacar o inimigo de vários ângulos diferentes.", "Médio alcance", null, null, "影擬人の術かげぎじんのじゅつ", "Kagegijin no Jutsu", "Técnica da Silhueta das Sombras", "Jutsu de Representação da Sombra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Sombra Lunar", "Utilizando-se da luz da lua, o usuário manipula a sombra do inimigo, sendo capaz de criar um clone tangível idêntico à sombra do alvo que se alimenta do chakra da vítima esgotando-a duas vezes mais rápido quando utiliza uma técnica. Apesar de serem mudos e vazios de personalidade ou características específicas, os clones são capazes de copiar todos os aspectos do alvo, desde suas técnicas até suas capacidades físicas e estilo de luta e ficam mais fortes sob a luz da lua cheia. Eles ainda são capazes de mudar sua forma a fim de criar armas e ficam constantemente ligados ao alvo.", null, null, null, "月影の術", "Tsukikage no Jutsu", "Técnica da Sombra Lunar", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Sombra da Lua", "Após performar o selo de mão necessário, o usuário estica sua sombra na forma de um fino chicote que é capaz de capturar um inimigo desprevenido.", "Médio alcance", null, "Selo especifico da técnica", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Tempestade Misteriosa", "Quando sob a influência do chakra escuro, Setsuna e Shizuku combinam seus ninjutsu elementares para criar uma tempestade que poderia dispersar raios de luz, a fim de demolir áreas. Utilizar esta técnica drena rapidamente tanto o chakra escuro, quanto as reservas de chakra normal.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Tempestade de Areia", "Com o auxílio de areia pré-existente do local, o usuário cria uma tempestade de areia em uma determinada área, circundando os seus alvos e limitando o seu campo visual.", "Curto a Médio alcance", null, "Selos especificos da técnica → Cobra", "砂嵐の術さなあらしのじゅつ", "Sunārashi no Jutsu", "Técnica da Tempestade de Areia", null, null, "Jutsu Tempestade de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Transformação em um Sapo", "Ao tocar a testa de um alvo e, em seguida, de acordo com uma forma da imagem mental do usuário, o alvo se transforma em um sapo. Isto é usado principalmente para interrogar vários prisioneiros de guerra. Vendo seus companheiros transformados em um sapo diante de seus olhos, os prisioneiros de guerra cedem à pressão, tornando-se o que poderia ser descrito como um 'coro de sapo', sonoramente cantando informações. Esta técnica aterrorizante faz, sem dúvida, um 'vazamento de informações' abrir.", "Curto alcance", "Rank C", "Tigre", "蛙変えるの術", "Kaeru Kaeru no Jutsu", "Técnica de Conversão em Sapo", null, "Jutsu da Transformação em Sapo", "Arte Ninja: Transformação de Sapo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Vibração de Insetos", "Esta técnica utiliza os kikaichū como um escudo, de modo que eles voam em forma de cúpula em alta velocidade, semelhante aos Oito Trigramas Palmas Giratórias do Céu dos Hyūga. Após o utilizador terminar os selos de mão, a cúpula é suficientemente forte para destruir os ataques recebidos, mas pode ser interrompida com uma força explosiva.", "Curto alcance", null, "Javali → Macaco → Dragao → Carneiro → Cão", "蟲瓶の術", "Mushi Kame no Jutsu", "Técnica da Vibração de Insetos", null, null, "Jutsu Parede de Insetos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica da Vontade do Tigre da Chuva", "Um ninjutsu de percepção, que permite o usuário manipular livremente a chuva infundindo chakra nela. A chuva cai das nuvens pesadas, formadas pelo usuário, sendo que as gotas estão estreitamente ligadas aos seus sentidos. Quando a chuva é obstruída por um chakra não pertencente a alguém da aldeia, a existência do intruso pode ser detectada. A chuva continua até um selo de cancelamento ser usado. Pain fazia chover todos os domingos em Amegakure, ou sempre que ele tinha que deixar a aldeia.", "Todos os alcances", "Rank –", "Cancelar: Passaro → Rato → Boi → Macaco → Rato → Cobra", "雨虎自在の術", "Ukojizai no Jutsu", "Técnica da Vontade do Tigre da Chuva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica das Cem Forças", "Sakura desfere um soco no inimigo com o Punho Adamantino, lançando ele para longe. Depois ela corre até o adversário e novamente entrega um soco, dessa vez mandando-o para cima. Por fim, vindo de cima, Sakura usa uma versão mais forte do Impacto da Flor de Cerejeira, destruindo o oponente e tudo ao seu redor.", "Curto a Médio alcance", null, null, "百力の術ひゃくちからのじゅつ", "Hyaku Chikara no Jutsu", "Técnica das Cem Forças", "Jutsu das Cem Forças", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica das Cordas de Sombra", "O Grupo Magaki envolve o oponente ao redor com uma corda preta.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica das Espadas Trovão: Controle Remoto", "Esta técnica permite ao usuário manipular o Kiba independentemente de longe. Isso permite que o usuário mantenha as mãos livres para o combate e também ataque de vários ângulos de uma só vez. O usuário também pode manipular as lâminas para atacar de várias maneiras.", "Curto a médio alcance", null, "Boi → Dragao → Macaco → Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica das Espadas de Trovão: Faísca", "Segurando as espadas Kiba acima de sua cabeça, como para-raios, o usuário canaliza um raio do céu para dar um choque imediato em qualquer oponente que esteja próximo do usuário.", "Todos os alcances", null, null, "雷刀術・落雷", "Raitō Jutsu: Rakurai", "Técnica das Espadas de Trovão: Faísca", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica das Forças Aliadas Shinobi", "A Técnica das Forças Aliadas Shinobi é uma formação de combate concebida por Shikaku Nara, usando as habilidades únicas das Forças Aliadas Shinobi para combater o Dez-Caudas.", "Todos os alcances", null, null, "忍連合軍の術しのびれんごうぐんのじゅつ", "Shinobi Rengōgun no Jutsu", "Técnica das Forças Aliadas Shinobi", "Jutsu das Forças Aliadas Shinobi", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica das Quatro Pernas", "Esta é uma transformação do clã Inuzuka que permite ao usuário incorporar características do seu cachorro e usá-las ofensivamente.", null, "Rank D", null, "四脚の術", "Shikyaku no Jutsu", "Técnica da Imitação da Fera", null, null, "Jutsu Quatro Patas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Absorção da Alma", "A Tecnica de Absorção da Alma é uma poderosa habilidade ortogada àqueles que despertaram o poder do Rinnegan.", "Curto alcance", null, null, "吸魂の術きゅうこんのじゅつ", "Kyūkon no Jutsu", "Técnica de Absorção da Alma", "Jutsu de Sugar Almas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Absorção da Névoa Sangrenta", "O usuário passa seu sangue pela Samehada, criando um selo de contrato. A lâmina desencadeia uma névoa vermelha espessa no campo de batalha, absorvendo o chakra das feridas abertas de todos os oponentes.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Absorção do Selo de Bloqueio", "Uma técnica de selamento altamente avançada concedida pelo Caminho Preta, que é capaz de absorver qualquer jutsu, independentemente de qualquer forma ou transformação da natureza, girando o fluxo de chakra interno, na direção contrária. Como resultado, esta técnica pode absorver qualquer técnica baseada em ninjutsu, independentemente do poder, sem prejuízo para o utilizador. Se o usuário chegar perto o suficiente para agarrar o adversário, a barreira pode absorver chakra direitamente para fora do corpo de um indivíduo, efetivamente drenando-os.", "Curto alcance", "Rank –", null, "封術吸印ふうじゅつきゅういん", "Fūjutsu Kyūin", "Técnica de Absorção do Selo de Bloqueio", "Jutsu do Selo Inalador, Ninjutsu de Absorção", null, "Jutsu de Absorção");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Afogamento da Bolha de Água", "Depois de liquefazer uma parte de seu corpo, Suigetsu manipula a água resultante e cerca a cabeça de seu alvo, impedindo-o de respirar, a fim de: tornar a vítima inconsciente ou matá-la, em última análise, se o sufocamento for sustentado.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Alteração do Terreno", "Usando seu Pergaminho de Diagrama Alterador de Terreno, Fuen mapeia uma área e, em seguida, é capaz de modificá-lo fisicamente a sua vontade ao desenhar adições ao terreno.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Anexamento", "A Técnica de Anexamento é uma habilidade ninja padrão que permite selar uma variedade de itens usando fūinjutsu.", "Curto alcance", null, null, "封入の術ふうにゅうのじゅつ", "Fūnyū no Jutsu", "Técnica de Anexamento", null, null, null);
