INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Bater-Na-Toupeira", "Ao usar a Liberação de Terra: Técnica de Esconder-se Como uma Toupeira para se deslocar no campo de batalha, o usuário aparece na frente de seu alvo antes de batê-lo com uma vara. Ao contrário de suas técnicas parentes, esta técnica é usada para atacar rapidamente vários adversários em sucessão.", "Curto alcance", null, null, "土竜叩きの術もぐらたたきのじゅつ", "Mogura Tataki no Jutsu", "Toupeira das Varetas", null, null, null);
