INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Calamidade da Descarga Relâmpago", "Uma técnica de colaboração usada por um usuário de Liberação de Água e um usuário de Liberação de Relâmpago. O usuário de Liberação de Água cria uma grande poça de água, em seguida, o usuário de Liberação de Relâmpago eletrocuta o oponente, causando uma explosão.", null, null, null, "放流雷禍の術ほうりゅうらいかのじゅつ", "Hōryū Raika no Jutsu", "Técnica de Calamidade da Descarga Relâmpago", "Desastre da Descarga Trovão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Cintilação Corporal", "A Técnica de Cintilação Corporal é uma técnica de movimento em alta velocidade, permitindo que um ninja mova-se de uma curta para uma longa distância a uma velocidade quase indetectável. Para um observador, ele aparece como se o usuário tivesse teletransportado. Uma baforada de fumaça é ocasionalmente usada para disfarçar os movimentos do usuário. Ela é realizada usando chakra para vitalizar temporariamente o corpo e se mover em velocidades extremas. A quantidade de chakra necessária depende da distância total e elevação entre o usuário e o destinatário.", null, "Rank D", "Carneiro", "瞬身の術", "Shunshin no Jutsu", "Técnica de Cintilação Corporal", null, "Técnica do Deslocamento Instantâneo", "Jutsu de Teletransporte");
