INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Clonagem", "A Técnica de Clonagem é um ninjutsu que cria uma cópia intangível de seu próprio corpo, sem qualquer substância. Uma vez que o próprio clone não tem a capacidade de atacar e, assim, só pode ser usado para confundir o inimigo, que é usado principalmente em combinação com outros ninjutsu. É uma técnica básica, mas dependendo da engenhosidade, pode ser utilizada de forma eficaz. Os clones podem não se dissipar quando eles entram em contato com algo.", null, "Rank E", "Carneiro → Cobra → Tigre", "分身の術", "Bunshin no Jutsu", "Técnica de Clonagem", null, null, "Jutsu de Clonagem");
