INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Cópia de Rosto", "O usuário olha para uma pessoa para copiar sua aparência. É uma técnica de transformação impecável, a família da pessoa copiada não saberia dizer a diferença e ninken não sentiria um cheiro diferente. Embora o usuário copie o rosto e corpo da pessoa, eles não podem usar suas técnicas ou habilidades únicas e eles também são muito fracos para atacar. Para copiar uma aparência, o usuário precisa usar uma máscara especial e ficar perto do alvo por um longo tempo. Quando a técnica começa, os olhos do usuário começam a brilhar. A transformação dura apenas poucos dias. Esta técnica é exclusiva do clã Kedōin.", null, null, null, "顔写しの術", "Kao Utsushi no Jutsu", "Técnica de Copiar o Rosto", null, null, "Jutsu de Copiar o Rosto");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Demolição do Demônio Parasita", "Uma técnica de assassinato parasita que Sakon e Ukon podem usar com o seu selo amaldiçoado nível 2 ativo, e sua kekkei genkai única. Com o poder do chakra, ele desmonta seu corpo até um nível celular, e até mesmo a um nível proteico, e entra na carne do inimigo. Essa técnica cruel gradualmente corrói as células corporais internas daqueles que ele se funde, e a morte segue.", "Curto alcance", null, null, "寄生鬼壊の術きせいきかいのじゅつ", "Kisei Kikai no Jutsu", "Técnica de Demolição do Demônio Parasita", null, "Técnica da Destruição Parasita Demoníaca", "Jutsu do Demônio Parasita");
