INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Deslacramento: Campo de Água Portátil", "Após abrir um pergaminho e coloca-lo no chão, o usuário executa os selos de mão necessários e coloca o pé no meio dele, convocando uma grande quantidade de água que cobre uma vasta área. A quantidade de água convocada pode variar desde um lago até um oceano, dependendo da quantidade de água que foi armazenada anteriormente.", null, null, "Boi → Coelho", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Deslacramento: Captura", "O usuário joga um pergaminho em direção ao seu oponente, que dispara uma cortina de fumaça para obstruir sua visão, em seguida o mesmo pergaminho dispara uma rede para captura-lo. A única forma de escapar dessa técnica é saltando para trás.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica de Deslacramento: Corda", "O usuário utiliza uma Etiqueta de Selamento para liberar cordas a fim de amarrar seu inimigo. Uma vez que algo ou alguém é interceptado pelas cordas, o papel o suga, ficando imobilizado sem poder realizar nenhuma ação no processo.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
