INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "A Lenda de Um Ninja Determinado", "A Lenda de Um Ninja Determinado foi o primeiro livro de Jiraiya, escrito ao treinar os órfãos de Ame. A história segue as aventuras de um ninja chamado Naruto, nomeado por Jiraiya enquanto ele comia ramen. O personagem principal, Naruto, nunca desiste e promete quebrar a 'maldição' que foi mencionado na história. A 'maldição' mencionada representa os 'ciclos viciosos' de guerra, conflito e ódio, que ocupa o mundo ninja que Nagato uma vez falou sobre com Jiraiya.", "ド根性忍伝", "Dokonjō Ninden", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Adaga", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Alto-Falante Ressonante de Eco", "Uma manopla de metal porosa que está localizada no braço direito de Dosu. Ela é usada para amplificar ou absorver as ondas sonoras provocadas por movimentos de seu braço. A melodia das ondas sonoras provocadas pelo braço podem ser manipuladas e dirigidas com chakra causando mais danos. Elas também podem causar visão borrada, tontura, bem como paralisar o alvo com os jutsu que são provenientes desta arma. Ele também pode agir como um bloqueio contra kunai e shuriken, que por sua vez resulta em mais vibrações que podem ser usadas para a técnica de Dosu.", "響鳴スピーカー", "Kyōmei Supīkā", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Antídoto", "Antídotos (解毒薬, Gedokuyaku) são tipos de medicamentos que agem como uma cura para vários venenos. Eles podem ser administrados a uma pessoa infectada por vários meios. Alguns venenos são complexos e, portanto, criar o antídoto vai exigir muita atenção ou então ele poderia matar a vítima; Sasori observou que ao fazer antídotos nem mesmo um erro da linha fina pode ser feito, ou então ele não iria funcionar. No entanto, especialmente treinados, gênios ninja médicos como Tsunade, Shizune ou Sakura Haruno podem realmente analisar e decifrar os muitos elementos diferentes usados ​​em venenos complexos e criar facilmente uma cura. Eles são geralmente um líquido engarrafado, mas pode ser qualquer coisa, desde uma flor ou em pó.", "解毒薬", "Gedokuyaku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Arco & Flecha", "Um Arco & Flecha (弓矢, Yumiya) é uma arma de condução usado para disparar flechas em um alvo distante. Esta arma consiste em um ou mais fios em ambas as extremidades ligadas a lados opostos de um sólido, parte flexível curva ou um conjunto de várias peças de bloqueio (compostos Arcos) que pode consistir de madeira, osso, fibra de carbono ou outra materiais. Jutsu baseado no uso de arco e flecha são chamados Kyūjutsu (弓术, literalmente significa 'Técnica Arco'). Kidōmaru por sua vez cria arcos e usando suas teias de aranha pegajosa de Ouro que lhe permite criar um arco poderoso que pode fazer melhor uso usando ambos os braços. Além disso, Uchiha Sasuke usado uma besta em seu Susanoo que é melhorada pelo tamanho e poder destrutivo. Depois de colocar o chakra das Bijū em todo o corpo de seu Susanoo, Sasuke pode formar um arco feito de puro raio que lhe permite atirar uma flecha de energia altamente destrutiva, que declarou como seu ataque mais poderoso. No Anime, Tenten usa uma espécie de arco com quatro flechas carregadas de uma vez. Para firmar este arco deve cair mecanismo de barreiras em vez de simplesmente liberar a corda. Ela usou -o para atacar à distância quando Gennō estava voando em sua ave gigante.", "弓矢", "Yumiya", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Areia", "Areia (砂, Suna), é um material versátil, mais frequentemente utilizado pelos shinobi proveniente de Sunagakure para diversos fins, incluindo várias funções complementares que vão desde a ocultação de disfarçar. No entanto, a própria ferramenta ganhou destaque nas mãos do jinchūriki do Shukaku, que adquiriu a habilidade de manipular a areia através da besta, com o mais famoso entre os quais está Gaara.", "砂", "Suna", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Areia de Ferro", "Areia de Ferro (砂鉄, Satetsu) é um conjunto de grânulos de ferro que podem ser usados como uma ferramenta através de uma habilidade única de converter seu chakra em forças magnéticas.", "砂鉄", "Satetsu", null, null, null, "Areia Ferruginosa");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Argolas de Captura", "São argolas semelhantes a chakrans, que são lançadas no intuito de capturar um oponente. Quando o usuário lança essa ferramenta ainda fechada, elas se abrem perto do alvo, com a parte simples passando pela dupla, até que fecham com a mesma mecânica e restringem o inimigo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura Infinita", "A Armadura sugando chakra.", "無限甲蓋", "Mugen Kōgai", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Batalha Shinobi", "Durante a era antes das vilas ocultas, os shinobi usavam uma armadura que era semelhante ao de um samurai. Ela proporcionava uma defesa maior do que o vestuário simples que os shinobi usariam em missões que não seriam para batalhas decisivas devido a ser feito de placas de armadura pesadas. Os shinobi de Konoha continuaram a usar a armadura até pelo menos a Primeira Guerra Mundial Shinobi, quando na segunda a transição para coletes começou.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Chakra", "A Armadura de Chakra (チャクラの鎧, Chakura no Yoroi) é um tipo único de armadura que drena chakra de ninjutsu e genjutsu, enquanto ao mesmo tempo amplifica o chakra do usuário. A armadura com o protótipo de cor preta foi usada por Dotō Kazahana.", "チャクラの鎧", "Chakura no Yoroi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Armadura de Vapor", "A Armadura de Vapor (蒸気の鎧, Jōki no Yoroi) é uma armadura de metal usada por Han, como instrumento de filtragem do vapor usado em suas técnicas.", "蒸気の鎧", "Jōki no Yoroi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Atirador de Dardos de Injeção", "Esta é uma das ferramentas utilizadas pelos ninjas médicos de Konohagakure. Assemelha-se a um fuzil de precisão rudimentar, que é usado para atirar projéteis, como tiros de injeção para desacordar o alvo. É constituída por um tubo de metal com aproximadamente o tamanho de uma régua com uma abertura em ambas as extremidades, como um tubo de descarga e está equipado com um espaço na parte superior e um laser de vista sobre a parte inferior. A arma é projetada para lançar seringas a longa distância, depois que o usuário pressionar a arma com a palma da mão, o dardo é atirado.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bō", "O Bō (棒, Literalmente significa: Bastão) é uma arma que é implementada na arte marcial japonesa e é a base do bōjutsu. Esta arma consiste em longa vara de madeira pura.", "棒", "Bō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Balista", "A balista é um lançador móvel de flechas maior que uma pessoa comum que é usado para acertar alvos a longa distância.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bashōsen", "O Bashōsen (芭蕉扇; Literalmente significa 'Leque do Palmo de Banana') é um grande leque capaz de gerar todas as cinco naturezas de chakra elementais básicas. Para fazer isso, o usuário só precisa dizer um comando mental, que produz quantidades substanciais do elemento desejado quando o leque é ondulado.", "芭蕉扇", "Bashōsen", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bastão do Seis Caminhos", null, "六道の棒りくどうのぼう", "Rikudō no Bō", "Bastão do Seis Caminhos", null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Benihisago", "A Benihisago (紅葫蘆; Literalmente significa 'Cabaça de Carmesim') é uma cabaça gigante de cor marrom com um pano vermelho em torno dela. É capaz de levar a palavra alma (言霊, kotodama) de uma pessoa refém dizendo o comando verbal 'registre' (録音しろ, rokuonshiro). Após a palavra alma ter sido atraída pela Kōkinjō e então cortada de seu corpo pela Shichiseiken, ela é absorvida na cabaça completando as condições da 'maldição' da vítima; se ela pronunciar a palavra que ela mais disse durante sua vida, ela vai ser sugada e presa dentro da Benihisago. Dizendo um homófono de sua palavra falada com mais freqüência, ou até mesmo não dizer nada por um determinado período de tempo, também vai provocar a sucção para a cabaça. Se a vítima começar a ser sugada para a Benihisago, a sucção vai parar se a sua palavra mais utilizada mudar. Kurama observou que tudo o que foi sugado para dentro da cabaça foi selada e não pode sair, não importa o que. Ele pode até mesmo manter os trouxe de volta com o Invocação: Reencarnação do Mundo Impuro e que será novamente selado após o invocador liberar a técnica.", "紅葫蘆", "Benihisago", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Besta (arma)", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bisturi", "Um bisturi é um pequeno instrumento de lâmina extremamente afiada destinada a cortar através da pele, tendões e músculos. Ele é usado principalmente por iryō-nin, médicos, ou praticamente qualquer pessoa com conhecimentos médicos para realizar cirurgias. No entanto, devido à sua agudeza, ele também pode ser usado como uma arma potente contra o inimigo. Kabuto Yakushi tinha conhecimento para fazer isso em uma tentativa de matar Sasuke.", "メス", "Mesu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bola de Papéis Explosivos", "Esta é simplesmente uma porção de papéis explosivos envoltos em uma bola e usado da mesma maneira que uma bala de canhão com um efeito explosivo. Eles são disparados a partir de canhões e são capazes de causar grandes danos. Também é usado como minas submarinas.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba de Fumaça", null, "煙玉", "Kemuridama", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba de Gelo", "Estas bombas são esferas cristalinas e azuis que quando detonadas, lançam uma torrente de espinhos de gelo. O gelo se explode com força suficiente para levantar uma grande quantidade de terra do chão. Fubuki prendeu essas bombas na kunai, e jogou-as em seus oponentes de cima ao voar com as asas em sua Armadura de Chakra.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba de Luz", "Bombas de Luz (光玉, Hikaridama) são usadas ​​para diversas coisas, criando uma luz ofuscante quando detonada. Ela pode se parecer com pequenas esferas embrulhadas em papel com o caractere 'hikari' (光, luz) ou, mais convencionalmente, como vasilhas. No anime, foi mostrado que as bombas de luz também pode ser usadas na forma de papel semelhante a papéis explosivos.", "光玉", "Hikaridama", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Bomba de Pimenta", "Estes são os itens usados ​​pelos aldeões da Vila da Árvore Genjutsu para repelir o genjutsu causada pela floresta circundante. Como a área é cercada por árvores que naturalmente lançam genjutsu nos moradores, por isso criaram estas bombas, a fim de ajudá-los a quebrar a técnica. Ela também é capaz de quebrar genjutsu feito pelas pessoas.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Boneco Misterioso", "Boneco Misterioso é uma ferramenta especial usada pelo Feiticeiro.", "謎の人形", "Nazo no Ningyō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Boneco de Metal", "O Boneco de Metal é um boneco gigante feito de metal que pode ser invocado por Tenten. O boneco é bastante pesado, visto quando Rock Lee teve dificuldades de levantá-lo, e para fazer isso, ele teve que usar um dos seus oito portões, quando o boneco o afogou durante um treino em uma cachoeira.", "金属の人形", "Kinzoku no Ningyō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braceletes de Ferro", "Os Braceletes de Ferro são um par de apêndices de ouro que ficam em volta do braço do usuário. Eles podem ser utilizados tanto como uma ferramenta de treinamento quanto como uma arma ofensiva na batalha, pois têm saliências que podem se sobressair. O da esquerda foi reduzido a cinzas pelo Amaterasu de Sasuke junto com o braço de A. Desde o início da Quarta Guerra Mundial Ninja o Raikage parou de usá-los.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Brahma", "O Brahma é um selo especial desenvolvido no Monte Myōboku pelos sapos que residem lá. Este selo, por sua vez, é raramente criado, visto que todo o poder inserido dentro dele é a vitalidade dos sapos que o desenvolvem junto de todo seu poder sábio.", "ブラフマー", "Brahmā", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Brasa Oculta na Boca", "Ao entrar no campo de batalha, Ibiki pode armazenar uma pequena quantidade de brasa em sua boca. A brasa deve ser mordida para ser ativada, e assim o ninja pode cuspir a brasa em uma substância combustível para ajudá-lo a acendê-la, criando fogo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braço de Cabo Retrátil", "O Braço de Cabo Retrátil é uma arma que permite que o usuário lance uma mão de metal que pode agarrar, arrastar e bater no inimigo. Ela pode ser retraída imediatamente.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Braço de Perfuração Mecânica", "O efeito da arma.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Broca de Braço Montada", "Uma peça não convencional de armamento utilizado por shinobi do Clã Tenrō de Yumegakure.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Buraco do Espaço-Tempo", "O Buraco do Espaço-Tempo é o espaço negativo que pode engolir tudo em torno dele. Ele apenas pode ser convocado por um membro da família real de Temujin na câmara onde o Livro de Gelel descreve, através da oferta de uma pequena quantidade de sangue do membro, a fim de destruir o filão de Gelel, mas o invocador também morrerá devido a nada que foi sugado para o caminho nunca voltou vivo. Temujin convocou o Caminho do Espaço-Tempo para selar o pilão de Gelel, quando a derrota de Haido acidentalmente destruiu a chave, fazendo com que o filão sair do controle. Ele estava pronto para aceitar sua morte, mas Naruto o salvou de ser sugado usando uma barragem de clones de sombra.", "時空の穴", "Jikū no Ana", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cabaça de Areia", "Gaara leva em torno de si uma cabaça gigante coberta de desenhos e preenchido com sua areia infundida com chakra que ele pode manipular livremente. A cabaça e a tampa também são feitas de areia e podem ser quebradas e usadas para executar técnicas também. Kiba notou que a areia da cabaça tem um forte cheiro de sangue, devido ao número de pessoas que Gaara matou com ela.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cachimbo de Bolhas", "O soprador de bolhas foi utilizado por Utakata para que ele executasse seu Ninjutsu de Bolha de Sabão. Era dourado em sua cor, dobrado no meio e tinha um pendão anexado. O soprador podia criar bolhas para muitas finalidades e também poderia ser usado como uma arma branca, já mostrado como muito resistente, sendo capaz de bloquear uma kunai sem nenhum dano sofrido aparente. Com Utakata sendo o usuário, o soprador melhorava e ajudava na manipulação das técnicas do Saiken.", "シャボン玉用パイプ", "Shabondama-yō Paipu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Caixa da Felicidade Suprema", "A Caixa da Felicidade Suprema (極楽の匣, Gokuraku no Hakō) é uma caixa gigante com uma cara de máscara demoníaca em cada um dos quatro lados que é localizada em Kusagakure.", "極楽の匣", "Gokuraku no Hakō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Canhão", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Canhão da Cabeça de Leão", "O Canhão da Cabeça de Leão é usado por Chiyo como um meio para o uso da Técnica de Selamento: Rugido Final do Leão. Ele é mantido dentro de um dos bonecos da Coleção de Marionetes de Chikamatsu. Ele começa como uma pequena bola que pode ser empunhada em uma mão, mas, quando jogada, cresce em uma cabeça de leão no tamanho de um homem com vários dentes grandes e uma boca extremamente aberta. Se destinada corretamente, a ferramenta irá fixar o alvo contra uma parede com a sua boca, tornando-o incapaz de se mover.", "獅子首観音", "Shishikubi Kannon", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Canhão de Bomba de Chakra", "Um dos prisioneiros de Orochimaru possuía um canhão em seu ombro, que ele usava para atirar bombas em seu oponente usando seu chakra.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Canhão de Chakra", "Canhões de Chakra foram um conjunto de armas de longo alcance especializadas que disparavam feixes de chakra. Essa arma é carregada com chakra de grupos de shinobi. Estes equipamentos foram utilizados para destruir a Lua, a qual estava caindo em direção à Terra por causa de Toneri Ōtsutsuki. Essa foi uma arma ocultada pelo Quarto Raikage, após a Quarta Guerra Mundial Ninja. Por questões de disparo, o equipamento consistia de três bocais (canos).", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Canhão de Kunai", "O Canhão de Kunai é uma arma de projétil utilizada pelos ninjas do País do Céu. Com ela é possível lançar várias kunai em alta velocidade nos adversários, semelhante à uma metralhadora. O canhão pode ser usado em conjunto com o dispositivo de asas mecânicas para realizar ataque aéreos com efeitos devastadores. Isto é conseguido, aparentemente, sem diminuir sua precisão.", "クナイ銃", "Kunai Jū", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cano Elétrico", "Esta ferramenta permite ao usuário disparar raios dela. Para ativá-lo, o usuário deve bater o cano no chão. Ele irá atirar um raio em seguida. Como explicado por Kakashi, isso é usado para dar a posição de um usuário para que seus companheiros de equipe possam se reagrupar uns com os outros. Outro uso que é mostrado é a capacidade de eletrocutar alguém ao bater o cano no chão.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cartões de Informações Ninja", "Cartões de Informações Ninja (忍識札, Ninshiki Kādo) são um tipo de ferramenta ninja onde a informação pode ser queimada sobre ele usando Chakra. Os cartões ao olhar são apagados a olho nu com o kanji para 'shinobi' (忍) na parte de trás, mas quando chakra é aplicado, diferentes categorias de dados que vão desde informações sobre as Cinco Países Shinobi para os ninjas que os habitam são exibidos em o cartão, mesmo em 3-D. No entanto, somente a pessoa que originalmente mostrou os dados para o cartão é capaz de recuperá-lo.", "忍識札", "Ninshiki Kādo", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Catapulta de Shuriken Gigante", "É uma catapulta com seis shuriken gigantes.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chaves de Braço Vedantes", "Um conjunto de três chaves de braço de ouro, que foram utilizados para selar o braço direito de Danzō Shimura. Eles foram usados ​​para manter os múltiplos Sharingan e as células de Hashirama Senju — ambos os quais estavam embutidas no braço de Danzō — selados. Elas exigem algum tempo para serem removidas uma vez que cada chave é mantida no lugar por parafusos.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Chicote", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clube", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Clube Gigante", "Forma aérea da concha.", "ジャイアントクラブ", "Jaiantokurabu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cogumelo de Modelagem", "Os cogumelos multiplicados e em cores diferentes.", "黴びる茸", "Kabirudake", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Colar do Primeiro Hokage", "O Colar do Primeiro Hokage (初代火影の首飾り, Shodai Hokage no Kubikazari) foi um colar usado por Naruto Uzumaki ao longo da série. Ele foi feito de um cristal e uma vez usado pelo Primeiro Hokage. Se ele fosse vendido, três montanhas contendo as mais ricas minas de ouro, poderiam ser compradas com lucros.", "初代火影の首飾り", "Shodai Hokage no Kubikazari", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Colete", "Coletes são uma forma de proteção em vestimenta acolchoada projetada para fornecer pelo menos uma proteção parcial de coisas como um trauma de força-bruta, estilhaços e outros projéteis. Isto pode ser visto quando protege Iruka Umino de uma kunai e uma Fūma Shuriken, enquanto ele estava à procura de Naruto e o pergaminho dos selos e foi emboscado por Mizuki. Eles também podem prevenir lesões letais de espadas e outras armas, como visto durante a luta de Baki com Hayate Gekkō. Para a maior parte, eles têm substituído a armadura amplamente utilizada por shinobi durante a Era dos Estados Combatentes e da Primeira Guerra Mundial Shinobi. Eles normalmente são usados ​​por Chūnin e Jōnin sobre sua roupa normal na maioria das aldeias, embora o design varia entre as aldeias e também mudam ao longo do tempo. As jaquetas estão entre as peças mais emblemáticas de equipamentos shinobi. A maioria deles tem bolsos ou bolsas de pergaminhos, que podem ser usados ​​para armazenar pergaminhos, armas e suprimentos. Isso permite que um ninja acesse-os rapidamente e/ou silenciosamente com apenas uma mão, como se viu com o caso de Kakashi Hatake quando ele normalmente faz isso em segredo para extrair uma kunai.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corpo Artificial de Hashirama", "O Corpo Artificial de Hashirama (柱間の人造体, Hashirama no Jinzōtai) é um clone não-sensível de Hashirama Senju criado por Madara Uchiha, a fim de colher suas únicas habilidades. Ele está localizado na seção subterrânea do Cemitério das Montanhas.", "柱間の人造体", "Hashirama no Jinzōtai", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Corrente do Bastão de Vento", "A Corrente do Bastão de Vento (锁风棒 Kusari Fūbō) é uma arma empunhada por Tenten. É muito semelhante a uma curta chigiriki, e tem uma kusari-fundo ligada a cada uma de suas extremidades. Ele é invocado pelo menor pergaminho de Tenten. A rotação rápida das duas cadeias mostrou-se poderosa o suficiente para acabar com o capacete do Guerreiro Amaldiçoado.", "鎖風棒", "Kusari Fūbō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cortina de Veneno Inodora", "Ao usar um papel explosivo, o usuário dispersa um pó inodoro que quando inalado por seus alvos afeta seus cinco sentidos, retardando seus tempos de reação. Isso permite que os usuários se esquivarem de ataques livremente ao atacar seus oponentes.", "無臭の毒霧", "Mushū no Dokugiri", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Cristal Hexagonal", "O Cristal Hexagonal (六角水晶, Rokkaku Suishō) foi dado a Koyuki por seu pai, quando criança. Apesar de ter perdido a fé nas crenças de seu pai, ela ainda manteve o cristal com ele. Mais tarde, é revelado que o cristal era a chave de um gerador, que era capaz de para mudar o País da Neve e transformá-lo no País da Primavera.", "六角水晶", "Rokkaku Suishō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Curry da Vida", "O Curry da Vida (命 の カレー, Inochi no Kare) é um curry criado por Karashi antes de entrar para a família de Raiga Kurosuki. Devido ao seu sabor picante intenso, o curry de vida tem propriedades especiais que podem reviver uma pessoa inconsciente ou alguém prestes a morrer. Ele também pode fortalecer uma pessoa depois que ele é comido algumas vezes. No entanto, parece ter efeitos opostos sobre as pessoas que têm menor tolerância com alimentos picantes, como Neji Hyūga, e deixa-los inconscientes. É especialmente apreciado por Rock Lee, que afirma que ele havia sido 'salvo' pelo curry antes. Depois de derramar acidentalmente um pouco de álcool no curry e servi-lo para a equipe, Lee comeu e ele começou a realizar o Punho Embriagado. No final, Sanshō fez um curry tão intenso que a cor mudou para vermelho. Lee tenta comê-lo, mas foi interrompido por Neji e Naruto o arrastando de volta para Konoha.", "命のカレー", "Inochi no Karē", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dakō", "A Dakō (蛇行, Dakō) é um protótipo de uma arma que foi criada por Iō de Konohagakure. É uma lança em forma de cruz que se pode esticar-se, contrair-se e dobrar-se.", "蛇行", "Dakō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dardo de Injeção", "Esta espécie de dardo de tiro é usados ​​por ninjas médicos. Eles são preenchidos com uma substância química de ação rápida que nocauteia o alvo dentro de segundos. O Atirador de Dardos de Injeção permite que eles sejam disparados de longas distâncias com grande precisão.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Disparador de Senbon Montado de Pulso", "Este dispositivo é usado por Shizune para lançar senbon envenenadas em seu oponente com precisão mortal. Ele é composto por um dispositivo segmentado de cinco partes com barris na parte superior onde as senbon são carregadas e embebidas no interior dos barris, quando as cordas são puxadas. Os fios na parte de trás destes facilitam o lançamento das armas. Ele geralmente é mantido escondido debaixo da manga de seu quimono, em seu braço direito.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dispositivo de Comunicação e Transmissão de Chakra", "Este é um dispositivo utilizado pela Divisão de Inteligência das Forças Aliadas Shinobi, a fim de passar informações para os outros a partir da sede para os ninja no campo ou entre batalhões e vice-versa. Há duas variantes para o dispositivo:", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dispositivo de Rompimento de Chakra", "Uma ferramenta experimental de Yukigakure concebida para ser a técnica perfeita para absorver chakra, este dispositivo absorve o chakra de um alvo capturado quando ele é aplicado. O dispositivo também cria um campo de força em torno do oponente, o que impede que ele seja removido ou destruído. Essas habilidades impede o sujeito de realizar ninjutsu em questão de momentos depois de ser aplicada, bem como fazer o alvo perder consciência dos acontecimentos da batalha momentaneamente.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dispositivo de Transporte da Lua", "Este dispositivo é utilizado para o transporte aéreo na Lua. Qualquer um que monta-se sobre ele, será transportado com segurança até seu destino.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Dispositivos Mecânicos Alados", "O lançador de kunai.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Droga Secreta de Orochimaru", "A Droga Secreta de Orochimaru é uma poção de Orochimaru, que deu instruções a Mizuki de como criá-la.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Droga de Besta com Cauda", "São comprimidos em uma forma encapsulada criados por Magire e Kido Tsumiki. Através da recolha de amostras de DNA deixadas por Naruto Uzumaki após sua batalha com Sasuke Uchiha no Vale do Fim, Kido e Magire obtiveram sucesso sendo capazes de criar esta droga. Após o consumo desta, o usuário passa por uma elevada transformação.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Drones Espelhados", "Essa Ferramenta Ninja Científica é um drone no formato de disco que pode ser controlado remotamente com chakra para flutuar e atacar. Ao possuía quatro, os quais usou parar disparar algumas técnicas, que foram fortes o suficiente para deixar o alvo inconsciente. Quando não usados em batalha, ele mantém os drones flutuando alto no céu.", "御鏡ドローン", "Mikagami Dorōn", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Enciclopédia Médica do Clã Nara", "Esta é uma grande enciclopédia médica mantida pelo clã Nara. Na frente, o livro tem o kanji para 'veado' (鹿, shika) de um lado e do outro o símbolo do clã. Os membros do clã são aparentemente qualificados no domínio da medicina e, como tal, têm criado e mantido uma enciclopédia com diferentes ervas medicinais, preparações e os seus efeitos como os três comprimidos coloridos, partilhados com o clã Akimichi. Este livro foi fundamental para salvar a vida de Chōji Akimichi depois de ele ingerir o último dos comprimidos durante sua batalha contra Jirōbō.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Enciclopédia de Patas", "A Enciclopédia de Patas é um livro cheio de pegadas de gatos recolhidas por Itachi Uchiha e Sasuke Uchiha em suas juventudes. As patas de gato vêm de patas de gatos domésticos até as da Matatabi. Ele foi originalmente um jogo que Itachi conjurou para distrair Sasuke que muitas vezes tinha tédio, enquanto ele comprava armas para o clã Uchiha. Na primeira Itachi não tinha a intenção de que o jogo seria sério, mas Nekobaa mais tarde transformou-o em um livro real. A última impressão da pata recolhida foi a de Nekomata, da qual foi obtida por Sasuke.", "肉球大全", "Nikukyū Taizen", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Equipamentos de Hassaku Onomichi", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Equipamentos de Ichirōta Oniyuzu", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Equipamentos de Kyohō Fuefuki", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo Retrátil", "Este é um escudo pessoal de Temujin. Fechado, parece uma pulseira. Uma vez aberto, pode repelir todos os tipos de armas convencionais, tais como kunai ou shuriken.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Escudo Sakazuki", "É um escudo empunhado por Gamaken.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esfera Explosiva", "A Esfera Explosiva (爆砕玉, Bakusaidama) é uma ferramenta ninja armazenada em um dos pergaminhos de Tenten. É uma esfera de metal com kunai soldadas dentro dela, que também tem um papel explosivo oculto que faz com que a esfera exploda ao entrar em contato com algo, enviando kunai e fragmentos da esfera como estilhaços para ferir o adversário.", "爆砕玉", "Bakusaidama", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Esferas Espirituais", "As Esferas Espirituais foram uma coleção de cinco esferas de cores diferentes, que eram, na verdade, a materialização da vontade da Marionete Mestre. As esferas manipularam os membros do clã Shirogane em buscá-las e provocar o renascimento da marionete.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada", "Espada (刀剣, Tōken) é uma arma de longa lâmina utilizada para o combate. Enquanto a maioria das espadas em Naruto são as tradicionais katana, tantō ou ninjatō japonesa, as espadas usadas por personagens principais tendem a ter pouca semelhança com armas reais, como é o caso com as espadas dos Sete Espadachins da Névoa.", "刀剣", "Tōken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Cavaleiro", "A Espada de Cavaleiro é espada pessoal de Temujin, e até agora só foi vista no segundo filme Naruto. Sua aparência normal é a de uma espada curta de estilo europeu, geralmente retratado sendo usada por cavaleiros, porém a lâmina da espada pode estender-se, tornando-se uma espada de corpo inteiro. Além de usar a espada normalmente, Temujin pode canalizar energia Gelel na espada, aumentando drasticamente o seu alcance de ataque e permitindo-lhe realizar várias técnicas com ela, como por exemplo o Trovão Furioso.", "ナイトソード", "Naitosōdo", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Fogo", "A Espada do Fogo foi utilizada por Hōki, e depois por Seimei. Ele é capaz de emitir e livremente manipular grandes quantidades de fogo, capazes de incinerar o que estiver em seu caminho. Ele pode ser usada para enviar feixes de luz, como os sinais através da esfera verde no final da lâmina. Ela também pode ser usado para chamar a Armadura Infinita, a Espada de Garian, e as Espadas de Dois Gumes.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Garian", "A Gariantō (ガリアン刀, Gariantō) é uma espada com um fio de metal passando pela lâmina. Os fios podem controlar os movimentos da lâmina e expandi-la para chegar ao alvo desejado. Além disso, cada uma das lâminas da espada tem a capacidade de reagir com o chakra do utilizador, ampliando-a, mudando sua forma para a de um dragão, e manipulá-la livremente. A espada também é usada como parte da Barreira de Ferramentas Ninja: Formação de Escamas Reversas, um konbijutsu.", "ガリアン刀", "Gariantō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kagutsuchi", "A Espada de Kagutsuchi é uma espada de chamas negras empunhada por Sasuke Uchiha.", "加具土命の剣カグツチのつるぎ", "Kagutsuchi no Tsurugi", "Espada da Ferramenta Adicionada do Senhor da Terra", "Espada de Controle de Chamas", null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kusanagi (Orochimaru)", "A Espada de Kusanagi é a Kusanagi da lenda japonesa. Orochimaru convoca sua Kusanagi abrindo sua boca e estendendo uma cobra que então abre a boca e 'vomita' a espada. Orochimaru foi visto produzindo a espada primeiro para que ele pudesse usá-la livremente, ou a lâmina primeiro a atacar seu oponente instantaneamente.", "草薙の剣", "Kusanagi no Tsurugi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Kusanagi (Sasuke Uchiha)", "Espada de Kusanagi é como Sasuke Uchiha chama sua chokutō. Ela possui uma bainha preta e punho combinando (ambos são azul no anime), e tem um tamanho maior do que um chokutō normal. Apesar disso, não tem nenhuma das especialidades naturais como a Espada de Kusanagi de Orochimaru exerce, tais como a extensão da lâmina e a capacidade de cortar através de quase tudo, ambos os quais são naturais atributos da Kusanagi, mas tem sido repetidamente demonstrado ser muito durável.", "草薙の剣", "Kusanagi no Tsurugi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Nunoboko", null, "ぬのぼこの剣ぬのぼこのけん", "Nunoboko no Ken", "Espada de Nunoboko", null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada de Totsuka", "A Espada de Totsuka, também conhecida como Sakegari no Tachi (酒刈太刀, Espada Longa Cortadora de Saquê), é uma variação da Espada de Kusanagi. Ela é embainhada numa jarra de saquê; a lâmina é de fato o líquido dentro do recipiente que é solto e moldado, ao invés do que uma espada convencional. Ela é uma arma etérea, com uma lâmina encantada capaz de selar qualquer coisa que ela perfure. Aqueles que são perfurados por ela são sugados para dentro da jarra e presos num “mundo de sonhos embriagados” como um genjutsu por toda eternidade; e além disso, Itachi também conseguiu remover o selo amaldiçoado de Sasuke ao perfurar e remover Orochimaru do corpo de Sasuke. Orochimaru procurou pela espada por toda sua vida e Zetsu comentou que ele nunca pôde achá-la.", "十拳剣とつかのつるぎ", "Totsuka no Tsurugi", "Espada Longa de Dez Mãos", null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada do Deus do Trovão", "Esta espada foi possuída por Tobirama Senju. Foi roubada de Konohagakure por Idate Morino porque Aoi Rokushō disse que ele poderia se tornar um chūnin, se ele conseguisse pegar a espada. Aoi em seguida, tomou a espada para si até que foi destruída pelos esforços combinados do Time Kakashi.", "雷神の剣", "Raijin no Ken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espada do Sapo", "A Espada do Sapo é um equipamento utilizado pelos sapos, comumente uma simples tantō. Ela pode ser utilizada para aplicar golpes rápidos e certeiros em seus alvos. Gamabunta foi capaz de usar um golpe poderoso o suficiente para arrancar um braço de Shukaku. Gamakichi foi capaz de utilizar também a espada para meios defensivos, como ao repelir os projéteis dos vários Clones Assassinos do Dez-Caudas.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espadas Curtas Voadoras Crescentes", "Esta arma é um par de espadas curtas gêmeas criadas pela Vila Takumi. Elas são projetadas para responder a ataques e permitir ao usuário transformar o vento livremente. Elas também aumentam o poder de ataque do usuário para um limite superior. A maiores vantagens dessas espadas são que elas aproveitando o chakra do usuário e executar técnicas de vento de alto nível com um custo mínimo de chakra, consequentemente, o usuário podendo manter uma luta com grande período sem interrupções ou falta de chakra. Curiosamente, Seimei afirma que não é necessário o uso das duas espadas para realizar as técnicas, mas apenas para o controle quase perfeito de chakra, assim sendo possível usá-las separadamente, porém com mais gastos de chakra.", "不弱飛翔小剣", "Fujaku Hishō Shōken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espadas de Pedra", "As Espadas de Pedra  são espadas secretas do Monte Myōboku. Na cor cinza ardósia, as espadas têm um buraco circular com alças com um longo aperto. Uma corrente conecta o punho ao cabo da espada que se junta com a lâmina. A lâmina da espada possui severas marcas que lembram veias. Estas foram usados ​​por Jiraiya depois da Ilusão Demoníaca: Confronto do Canto de Sapo foi usada para perfurar os corpos dos seus alvos como uma cunha.", "石剣", "Ishiken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Espelho de Yata", null, "八咫鏡やたのかがみ", "Yata no Kagami", "Espelho de Yata", null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Estilingue", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Etiqueta de Controle", "Trata-se de uma etiqueta especial que é utilizada pelos detentores da Invocação: Reencarnação do Mundo Impuro, atribuída normalmente a Orochimaru e Kabuto, a fim de ampliar seus controles sob os cadáveres reencarnados.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Etiqueta de Selamento", "Ao colocar uma etiqueta de selamento sobre um oponente ou objeto, um selo substancial é gerado que impede que o artigo em questão seja invocado, com o contato a etiqueta original permanece intacta. O selo gerado irá se espalhar por todo o alvo e interceptar no lado oposto do local onde o selo está colocado, onde será então feito um nó no seu lugar. Eles podem ser aplicados a vários meios, tais como tecidos e até mesmo areia, como demonstrado por Gaara, bem como ligados entre si para tornar o efeito mais forte. Eles também podem ser jogados a partir de uma grande distância, anexando-los em uma kunai. As etiquetas podem ser eliminadas se o processo de selagem for incompleto, mas uma vez completo, a remoção da etiqueta parece ser extremamente difícil através de meios normais por qualquer um que não sabe como removê-lo.", "封印札", "Fūin Fuda", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Etiqueta de Tratamento", "As Etiquetas de Tratamento (治療札, Chiryō Fuda) são pedaços de papel estampados com uma fórmula especial e um kanji para 'tratamento' (療, ryō) centralizado. É uma ferramenta utilizada em tratamentos médicos.", "治療札", "Chiryō Fuda", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Extensão dos Olhos", "Deidara tem um dispositivo sobre o seu olho esquerdo, funcionando como um telescópio. Como ele é um lutador de longo alcance por natureza, o dispositivo permite que ele veja seus alvos à distância e lhe permite encontrar o intervalo entre ele e outros objetos. É geralmente escondido debaixo de seu cabelo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fūma Shuriken", "A Fūma Shuriken (風魔手裏剣, Fūma Shuriken) é uma grande shuriken, com quatro lâminas possuindo nitidez preeminente e letalidade. Este tipo de shuriken é considerada característica do famoso clã Fūma, conforme quais eles foram nomeados, e que, eventualmente, as desenvolveu. No entanto, a sua utilização não se limita a este clã. Suas quatro lâminas podem ser recolhidas para armazenamento fácil, levando ao seu apelido Moinho de Vento da Sombra (影風車, Kage Fūsha).", "風魔手裏剣", "Fūma Shuriken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Valiosas do Sábio dos Seis Caminhos", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fio de Aço", "O Fio de Aço é uma fina fibra constituída de aço, servindo a diversos propósitos. Por sua maleabilidade e por sua fina espessura — comparado a outros materiais maleáveis, seus usos são virtualmente infinitos, como por exemplo: manipular espelhos, manipular armas, conectar o usuário com o alvo a fim de executar uma técnica, segurar uma pessoa, rappel, estilingue, prender e restringir os movimentos de alguém, delimitar um perímetro, contra-atacar, cortar objetos duros — como madeira, músculos e ossos — e, principalmente, criar de armadilhas comuns e explosivas.", "鋼線", "Kōsen", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flauta Demoníaca", "Usando esta flauta, Tayuya pode lançar genjutsu, invocar e controlar seus Doki tocando melodias específicas. Este é o único item que ela mantêm consigo. Ela alegou que ninguém que já tenha ouvido o som de sua flauta viveu. Se o adversário reconhece sua melodia ela pode mudá-la para confundi-los. A flauta foi cortada pela metade durante sua batalha com Temari.", "魔笛", "Mateki", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flor Jofuku", "Gaara feito de pólen da flor.", "徐福花", "Jofukubana", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Foice de Lâmina Tripla", "A Foice Tripla é a principal arma do membro da Akatsuki, Hidan. Seu principal objetivo é auxiliá-lo na coleta de sangue para o seu ritual de combate e religião. Devido a isso foi concebido, não tanto para matar o alvo, mas para ferir eles, não importa quão grande ou pequeno o ferimento.", "三刃の大鎌", "Sanjin no Ōgama", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formiga Negra", "A Formiga Negra (黒 蚁, Kuroari ) é uma marionete, originalmente criada por Sasori, mas foi passada para Kankurō. Ele tem uma cabeça mais longa do que a do Corvo, com um rosto em forma de balde suportando dois afiados chifres vermelhos e seis braços; suas características faciais incluem três olhos semelhantes ao Corvo, com um corte de cabelo despenteado escuro. Seu corpo de quadro geral lembra um grande barril.", "黒蟻", "Kuroari", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Gama-chan", "Gama-chan (ガマちゃん, Gama-chan; Literalmente significa 'Sapo-chan') é a carteira em forma de sapo de Naruto Uzumaki. A presença da Gama-chan em breves aparições na série são usados ​​para momentos cômicos. Naruto se preocupa com Gama-chan e se refere a ele como se fosse um animal de estimação, dizendo que é feliz quando o mesmo está gordo e cheio de dinheiro. Naruto fica espantado com o dinheiro que Jiraiya tirou para a sua 'pesquisa', notando que 'Gama-chan' tinha ficado muito magro.", "ガマちゃん", "Gama-chan", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra de Lâmina Tripla Aprimorada com Chakra", "Este tipo de tekagi-shuko é uma arma usada por Sora e seu pai. É uma garra com três lâminas, que pode ser reforçada com o chakra do usuário, assim como a natureza de chakra do mesmo. Ele tem o mesmo efeito que as lâminas de chakra de Asuma Sarutobi.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Guarda-Chuva", "O guarda-chuva disparando múltiplas senbon.", "傘", "Kasa", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Gunbai", "Um Gunbai (軍配; Literalmente significa 'Arranjo do Exército') ou Gunbai Uchiha (軍配団扇; Literalmente significa 'Leque do Arranjo do Exército') é um leque não dobrável, geralmente feito de madeira. Foi usado no Japão antigo por oficiais samurais para se comunicar com suas tropas. Hoje em dia, ele é usado pelos árbitros de partidas oficiais de sumô.", "軍配", "Gunbai", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Hidro-Bomba", "Sasori pode disparar fluxos pressurizados de água através dos canos em suas palmas, semelhante a um canhão de água. Torcendo os pulsos, como botões, Sasori pode condensar os fluxos em cortadores de jato de água que pode cortar através das rochas. Como munição, Sasori também leva quatro pergaminhos em suas costas.", "ハイドロポンプ", "Haidoroponpu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Hiramekarei", "Hiramekarei (ヒラメカレイ, Hiramekarei; Literalmente significa 'Espadas Gêmeas') é uma espada dupla dos Sete Espadachins Ninja da Névoa.", "ヒラメカレイ", "Hiramekarei", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Hiruko", "Hiruko sem o manto da Akatsuki.", "ヒルコ", "Hiruko", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Icha Icha", "Icha Icha (イチャイチャ, Icha Icha) é uma série de romances mais vendidos escritos por Jiraiya. Pouco se sabe sobre o enredo, mas sabe-se ser sobre um amor apaixonado entre um homem e uma mulher. Kakashi Hatake é um grande fã da série e é mostrado lendo um deles a maior parte do tempo. Eles são cômicos (provavelmente adultos​​) e relatam experiências cômicas de Jiraiya no amor. Eles são destinadas para pessoas de 18 anos ou mais.", "イチャイチャ", "Icha Icha", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jōhyō", "O Jōhyō se consiste de uma longa corda, normalmente possui vários metros de comprimento, com uma alça presa a um fim, e um ferrão de metal ligado a outro, agindo como um peso que permite ao usuário para lançar o dardo para fora em um alvo de longo alcance para vinculá-las em seguida, bobina-los ou realizar um ataque contra eles.", "縄鏢 ou 縄標", "Jōhyō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jidanda", "A Jidanda é uma arma que foi criada por Iō usando dados que ele coletou de suas criações anteriores, Dakō e Tsurukame. É um gigante e cravada, bola de ferro que contém etiquetas explosivas dentro dela. Sua alça, como a Dako é capaz de esticar e pode até mesmo ser usado para atacar o inimigo. Os picos na bola também podem abrir para revelar as marcas de explosivos para ataques de surpresa. Devido ao seu tamanho, os papéis explosivos podem ser usadas para conduzir a bola no ar, usando o recuo e impulso da explosão. Enquanto no ar o portador pode estender o punho e balançar a bola para o inimigo como um mangual gigante. Depois de ver Tenten usá-lo na batalha, Iō pegou a Jidanda de volta para fazer ajustes.", "地団駄", "Jidanda", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kōkinjō", "A Kōkinjō (幌金縄; Literalmente significa 'Corda de Cobertura de Ouro') é uma corda grossa e comprida. Ao dizer o usuário o comando verbal 'Atar e Sugar' (しばり出せ, Shibaridase), ele é capaz de tirar a palavra da alma (言霊, kotodama) de qualquer pessoa que tenha entrado em contato com a corda, para que ela possa ser cortada pela Shichiseiken. O comando não é obrigatório e a Kōkinjō não tirará a palavra da alma do indivíduo se o comando não for dito. O item gera uma aura que pode tomar a forma de cordas adicionais, dando-lhe um maior alcance.", "幌金縄", "Kōkinjō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kabutowari", "A Kabutowari (兜割, 'Rachadora de Elmos'), é uma das espadas dos Sete Espachins da Névoa empunhada por Jinin Akebino, a espada em si consiste de um machado e um martelo gigante, ligados por uma corda fina de couro, que quando juntas as partes são referidas como 'Espada Brusca' (鈍刀, dontō) que diz ser capaz de quebrar qualquer defesa. Embora o comprimento do cabo significa que arma pode ser usada individualmente para cortar e esmagar obstruções, respectivamente sua verdadeira força reside na sua capacidade de ser empregada em conjunto. Ao se deparar com uma defesa particularmente difícil ou aparentemente impenetrável, o portador pode primeiro atacar batendo com o machado, antes de bater o martelo.", "兜割", "Kabutowari", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kama", "Kama é uma foice japonesa usada como arma. Ela tem um cabo curto e um bico pontiagudo com uma borda afiada que inflige danos. Há também uma variação da kama, o kusarigama, que tem uma corrente ligada a ela para aumentar a sua gama de ataque.", "鎌", "Kama", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Karasu", "Corvo (乌, Karasu) é uma marionete, originalmente criada por Sasori, mas o mesmo passou para Kankurō. Assemelha-se a um humanoide de três olhos, quatro braços com dentes irregulares e penteado marrom espetado saliente no topo de sua cabeça e lados enquadrados. Na Parte I, Kankurō normalmente mantém Corvo envolto em ligaduras nas costas, e ele é capaz de trocar de lugar com ele à vontade usando a técnica de substituição do corpo. Por causa da preferência de Kankurō, a instalação de mecanismos em seus fantoches, Corvo é muito ofensivo, tendo inúmeras armas escondidas.", "烏", "Karasu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Karasuki", "Karasuki (カラスキ, Karasuki) é um dispositivo tecnológico nobre de origem Ōtsutsuki similar à uma tartaruga que permite viajar no tempo.", "カラスキ", "Karasuki", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Katar", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kiba (espadas)", "Kiba (牙; Literalmente significa 'Presas') é uma das famosas espadas dos Sete Espadachins Ninja da Névoa. É uma espada dupla, a sua característica mais notável é uma saliência para cima, curvado perto da extremidade de um lado da lâmina e um outro perto da base do outro lado da lâmina.", "牙", "Kiba", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kohaku no Jōhei", "O Kohaku no Jōhei (琥珀の浄瓶; Literalmente significa 'Pote de Purificação Âmbar') é um pote grande que tinha a capacidade de selar qualquer coisa dentro dele, se o alvo responder quando chamado pelo portador do pote o pote registra sua voz e depois o sela. Esta função é semelhante a do Benihisago, mas mais avançada já que é necessária menos preparação para usá-lo. O pote tem um kanji estilizado para 'relâmpago' (雷, kaminari) sobre ele e uma corda grossa amarrada em torno de sua borda.", "琥珀の浄瓶", "Kohaku no Jōhei", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kokutō", "A Kokutō (黒刀; Literalmente significa 'Espada Negra') é uma espada única empunhada por Raidō Namiashi. A lâmina é uma katana com uma superfície escura, não-reflexiva e é fortemente revestido de veneno que Raidō geralmente usa para realizar suas missões de assassinato. Ele usou-a em conjunto com a Técnica de Dispersão dos Mil Corvos de Aoba Yamashiro, se escondendo e com a sua espada, dispersando com sucesso Kakuzu um membro da Akatsuki.", "黒刀", "Kokutō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Komushi (marionete)", "Komushi (コムシ, Komushi) é uma marionete humana usada por Sasori, enquanto ele ainda estava vivendo em Sunagakure. A marionete foi feita com o cadáver do único amigo de Sasori, Komushi após sua morte. Não há nenhuma evidência clara, mas suspeita-se que Sasori foi o responsável pela morte de Komushi para que ele pudesse fazer a primeira marionete humana.", "コムシ", "Komushi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kotarō", "Kotarō (虎打狼) é uma versão mais poderosa da classe comum dos fármacos saigenzai. Essa versão só pode ser preparada pelos membros do clã Kodon. Há três níveis de potência, sendo:", "虎打狼", "Kotarō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kote", "O Kote (小手, Literalmente significa: Antebraço) é uma 'Arma Ninja Científica' (科学忍具, Kagaku Ningu) desenvolvida por Katasuke do Time Científico de Armas Ninja de Konoha.", "小手", "Kote", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kubikiribōchō", "A Kubikiribōchō (首斬り包丁; Lâmina do Executor) é uma espada enorme em forma de uma faca gigante de açougueiro, ganhando o título de 'Espada de Cortar' (断刀, dantō). Como as outras armas que pertencem aos Sete Espadachins Ninja da Névoa, a espada é passada de geração em geração entre os membros do grupo.", "首斬り包丁", null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kunai", "A Kunai assim como a shuriken, é uma das ferramentas mais utilizadas na série Naruto. É uma espécie de Adaga negra com o objetivo de pressionar e esfaquear, pode também causar danos ao ser lançada, apesar de não ser esse o seu objetivo. O seu tamanho é semelhante ao tamanho de uma mão.", "クナイ", "Kunai", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kunai do Deus Voador do Trovão", "A Kunai do Deus Voador do Trovão (飛雷神クナイ, Hiraishin Kunai) são kunai customizadas (特注クナイ, tokuchū kunai) utilizadas exclusivamente por Minato Namikaze em conjunto com seu Ninjutsu Espaço-Tempo: A Técnica do Deus Voador do Trovão. Elas diferem de uma kunai padrão pelo fato de terem três lâminas, em vez de uma no cabo (que é mais grosso que o normal) está a 'marca' para a habilidade de teletransporte do Minato, Minato disse também que estas kunai são pouco mais pesadas do que uma kunai normal. As suas pontas também as tornam mais mortais em combate corpo a corpo.", "飛雷神クナイ", "Hiraishin Kunai", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kurosawa", "Kurosawa (黒澤; Literalmente significa 'Pântano Negro') é o nome da espada de Mifune. A espada é bastante longa e fina, e tem um guarda-mão retangular e um punho longo envolto em bandagens. Mifune tem mostrado grande habilidade e destreza em empunhar a espada, realizando Iaidō com a lâmina. Ela também foi usada para cortar e abrir a cabeça de Ibuse quando a salamandra o engoliu.", "黒澤", "Kurosawa", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kusari-Fundo", "A Kusari-Fundo (鎖憤怒, Literalmente significa: Corrente Furiosa) é um tipo de corrente com um peso em suas extremidades. O peso é em cada um dos seus pontos são usados para enrolar a corrente em torno do seu alvo, e ao fazê-lo cria um nó muito difícil de desarmar, de modo que também serve para bloquear espadas e outras armas — semelhante à maneira que Hanzō faz sua Kusarigama. Uma arma desenvolvida especialmente para fins suplementares, é muito útil para shinobi que necessitam bloquear ataques rápidos ou mudar sua trajetória no ar. Foi usada por Kakashi Hatake para agarrar uma superfície próxima e evitar o efeito de Bansho Ten'in de Nagato através do Caminho Deva. Uma vez que é condutor de eletricidade, também pode ser um condutor útil para técnicas de Liberação de Relâmpago, visto por exemplo quando Furui usou uma técnica baseada em relâmpago para parar Gyūki.", "鎖憤怒", "Kusari-Fundo", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kusarigama", "Um kusarigama é uma arma japonesa tradicional que consiste de uma foice ligada a uma corrente de peso. Ela pode ser usada para atacar oponentes, ou neutralizá-los com a corrente. Alguns usuários qualificados de kusarigama podem até mesmo roubar armas de seus oponentes. Tenten usou-o como um projétil em sua técnica Ascensão dos Dragões Gêmeos, enquanto Jiga magnetizou com sua Arte Ninja Magnética para roubar armas de seus oponentes. Hanzō é notado sendo um usuário muito hábil do kusarigama, que ele encharca de veneno para aumentar ainda mais sua letalidade. Raramente tendia a lançar ataques ao balançar a foice com a corrente ao invés de simplesmente utilizar o final com com peso da corrente, que é geralmente considerada a maneira mais eficaz.", "鎖鎌", "Kusarigama", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kuwa", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança-Chamas", "Sasori pode disparar fogo dos canos em suas mãos, tornando-os como lança-chamas. As chamas são ferozes o suficiente para derreter rochas e incinerar uma kunai de metal que foi lançada para ele perto instantaneamente. A munição é selada no primeiro pergaminho em suas costas. Durante o uso de Sasori sendo controlado por Kankurō, o pergaminho munição não é visto, o que implica que o lança chamas pode ser usado sem ele.", "火炎放射", "Kaen Hōsha", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança Retrátil", "A Lança Retrátil é uma arma usada por Hidan principalmente em seus rituais Jashin. A lança é um cilindro retrátil, que pode ser estendido ao seu tamanho total rapidamente. Ela é usada regularmente, como principal movimento de assassinato de Hidan durante seu ritual, ao esfaquear-se no coração. Os rituais de Hidan geralmente duram muito tempo devido a ele gostar de brincar com o seu adversário, tal como esfaquear-se em diversas áreas do seu corpo, para fazê-los sofrer antes de matá-los. Ela também pode ser usada para combate, tal como a tentativa desesperada de tirar sangue do Shikamaru, quando sua Foice Tripla foi perdida. É mostrado que ele esconde pelo menos, duas dessas lanças em seu manto da Akatsuki.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança do Céu", "A Lança do Céu é uma torre enorme como a estrutura oculta sob O Buraco.", "天の矛", "Ama no Hoko", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lançador de Pulso", "O Dragão de Fumaça.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lançador de Shuriken", "Esta ferramenta permite que o usuário acople várias shuriken para lançá-las em batalha.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Leque Dobradiço Gigante", "Esta arma empunhada por Temari é um gigante leque japonês de guerra, que tem três círculos roxos ao longo de seu corpo de metal que ela chama de 'estrelas' ('luas' na dublagem brasileira). A força das rajadas de vento que ela cria é indicada pelo número de estrelas mostradas (sendo uma a mais fraca, sendo três a mais forte). A ventoinha pode também ser usado como uma defesa ou para bloquear ataques. Também parece ser capaz de desviar kunai e shuriken quando aberto e também pode ser alojado no chão.", "巨大扇子", "Kyodai Sensu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lista de Tecnologias", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lista de Transportes", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Livro Bingo", "Os Livros Bingo são livros pretos usados ​​por shinobi, cuja contêm toda e qualquer informação sobre a Lista Negra Shinobi e de seus alvos. Há várias versões dos Livros Bingo, sendo as mais comuns as que se baseiam das Aldeias Ocultas.", "手配書", "Bingobukku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Livro de Gelel", "O Livro de Gelel (ゲレル の 書, Gereru no Sho) contém todos os segredos da Pedra de Gelel e como controlar seu poder, juntamente com a história do povo de Temujin e os desenvolvimentos que alcançaram com a ajuda do poder da Pedra. O Livro de Gelel conta a história de como o poder da Gelel veio a ser descoberto e usado pelos antepassados de Temujin. O livro foi passado pelos ancestrais de Temujin no mar e mais tarde Haido conseguiu a posse do livro (alegando que ele comprou de um comerciante nômade, para que Kahiko pensasse que era algo simples), e começou a usar seus segredos para fins malignos. O livro foi perdido quando ele foi deixado a bordo de aeronave de Haido, do qual foi sugado para o Caminho do Espaço-Tempo.", "ゲレルの書", "Gereru no Sho", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina do Dragão", "A Lâmina do Dragão (龍刃, Ryūjin) é uma espada usada por Naruto em Naruto Shippūden: Dragon Blade Chronicles e em Naruto Shippūden: Shinobi Rumble.", "龍刃", "Ryūjin", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâminas de Chakra", "As Lâminas de Chakra (チャクラ刀, Chakura Tō) são armas cortantes feitas de uma metal especial que pode ser infundido através do 'fluxo de chakra' com uma transformação da natureza ou chakra Yin-Yang para produzir efeitos adicionais. De acordo com Shikamaru, as lâminas podem absorver as características do chakra do usuário e se tornam a base para uma técnica.", "チャクラ刀", "Chakura Tō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâminas de Chakra de Konoha", "Lâmina com o chakra já canalizado.", "チャクラ刀", "Chakura Tō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâminas de Kunai", "Estas são as lâminas kunai empunhadas por Izumo e Kotetsu. Elas têm a aparência geral da Kunai com bases semelhantes, mas as lâminas em vez disso são curvas, alongadas e com arestas únicas, dando-lhes a função e aparência semelhante às espadas. Cada uma das lâminas tem um pequeno buraco perto de seu punho longo enfaixado, que, em sua borda ostenta uma versão maior do anel padrão de kunai que todos têm. Elas são normalmente transportadas por Kotetsu, onde são enfaixadas e atadas às costas. Izumo e Kotetsu podem conectar as duas lâminas com uma cadeia anexada a um peso, que eles podem usar para embrulhar um oponente.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Makibishi", "Makibishi são objetos pequenos e afiados que são usados ​​principalmente para inibir alguém de andar em uma determinada direção ou área, uma vez que lesionaria os pés dos ninjas se eles tentarem andar sobre eles.", "まきびし", "Makibishi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mangual", "O Mangual é uma arma que consiste em uma vara acorrentado a uma esfera metálica em forma de um quadrado com espinhos aparentemente é uma arma muito letal ao entrar em contato com o oponente. Suiko único conhecido por empunhar essa arma é um Ninja da Vila Takumi.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manopla de Petrificação", "Ishidate usa uma luva especial em sua mão direita que possui duas habilidades que podem ser utilizadas com algumas finalidades.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manoplas de Metal", "Estas são as armas escolhidas pelos Irmãos Demônios. Elas são um par de grandes manoplas , de metal — cada irmão usava uma — que também abrigava uma longa, corrente retrátil de shuriken correndo entre os dois, que é usada para ligar um inimigo e, em seguida, rasgar em pedaços. No caso da corrente de shuriken ser neutralizada, as manoplas têm também um mecanismo de libertação para liberar o usuário para ataques individuais com as garras afiadas nos dedos, envenenados para a letalidade adicional.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete Inseto do Zumbido da Abelha Rainha", "Marionetes Inseto do Zumbido da Abelha Rainha são um tipo especial de marionetes que são exclusivas do arsenal de Menō e foram criadas pelo clã Shirogane.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete Lagarto", "A Marionete Lagarto é uma marionete Rank S usado por Ibushi. É semelhante ao Sanshōuo em que se baseia em um lagarto e respira fogo. Seu uso era supostamente exclusivo apenas para os membros de alto escalão do Shirogane. O boneco foi finalmente destruído por Naruto Uzumaki e Kakashi Hatake.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete Vampira", "A Marionete Vampira é uma marionete alienígena de origens desconhecidas. Sanguinária, por natureza, a marionete bebe o sangue de seus alvos enquanto mata-os nos esgotos. A Quinta Hokage atribuiu a missão de destruí-la, dando-lhe um rank B em dificuldade.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete de Tiro de Água", "Estas marionetes são mantidas em pequenas caixas de madeira, ​​nas costas do portador e podem ser ativadas. Eles podem desconectar as mãos de seus braços através de dobradiças e atirar água a partir de tubos em seus braços. Elas são a base da técnica Liberação de Água da Marionete: Dança da Chuva Mística", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionetes", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionetes Andarilhas", "As Marionetes Andarilhas são uma forma única de marionetes criadas pelo clã Shirogane. Como eles diferem de fantoches normais não é claro, mas eles são o resultado da experimentação humana proibido. Eles vêm em três variações.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionetes Elementais", "As Marionetes Elementais têm aproximadamente a mesma aparência que as marionetes normais, mas têm a cor ou vermelha ou azul. Elas podem usar água ou chamas na batalha, e parece que cada uma pode ter apenas uma das duas habilidades. As marionetes foram usadas por Gandō para tentar parar Naruto e tomar a Esfera Espiritual Azul dele, de Kakashi e de Might Guy.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionetes de Barreira", "As Marionetes de Barreira são usadas pelo clã Shirogane para se proteger de ataques inimigos. Elas também podem ser usadas para coletar ou guardar as Esferas Espirituais de seus inimigos. As marionetes foram criadas e utilizadas por ambos Gandō e Menō dos Três Shirogane do clã Shirogane.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mecanismo de Kunai Escondida", "Este item permite ao usuário instantaneamente estender e retrair uma kunai de sua manga com um fio de mola.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Meteorito", "O Meteorito (星, Hoshi) foi um fragmento que, por motivos desconhecidos, caíra em Hoshigakure. Embora nada de relevante se conheça a seu respeito, o meteorito possuía uma aura de chakra roxo, em virtude do Poder do Meteorito. Esse poder influenciou na criação da cratera em Hoshigakure, tal como a emanação de um gás venenoso nas proximidades.", "星", "Hoshi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mãe e Pai", "'Mãe' (母, Haha) e 'Pai' (父, Chichi) foram as duas primeiras marionetes criadas por Sasori, modelado após seus verdadeiros pai e mãe, que foram mortos por Sakumo Hatake durante a Segunda Guerra Mundial Ninja. Embora criado para dar-lhe conforto, os bonecos não poderiam cumprir esse propósito, e Sasori desiludido eventualmente descartou-os quando ele deixou Sunagakure. Com sua partida, os fantoches entraram na posse de Chiyo, que os modificou para fins de combate.", "母と父", "Haha to Chichi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Máquina Voadora Destrutiva", "Esta é uma máquina voadora gigante. Ela é operada por três pessoas, enquanto uma quarta pessoa se senta em um compartimento extensível, onde se concentra o seu chakra em um feixe explosivo capaz de produzir destruição em massa. Foi usado pelo País do Céu em seu ataque a Konoha. Ela foi esmagada quando Shino Aburame usou seus kikaichū para devorar a frota, fazendo com que os detritos caissem na máquina.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Máquina de Amplificação de Leitura de Mente", "Este dispositivo é usado pelo Time de Análise e está localizado na Divisão de Inteligência de Konohagakure. Ele é usado para amplificar a técnica de Leitura de Mente. Quando este dispositivo é usado, uma pessoa deve ficar em contato direto com o alvo, enquanto outros também devem ajudar, colocando suas mãos nos selos localizados no dispositivo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Máscara Shinigami", "Uma máscara que se assemelha a face do Shinigami, que residia em uma parede dentro do Templo Armazenador de Máscara do Clã Uzumaki localizado na periferia de Konohagakure, juntamente com outras 26 máscaras parecidas. Ela permite que o usuário invoque o Shinigami por possuir o corpo do usuário. Isso permite que o usuário execute o Selo Consumidor do Demônio Morto: Liberar.", "死神の面", "Shinigami no Men", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Máscara da Maldição", "A máscara quebrada.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Máscara de Gás", "A Máscara de Gás é um equipamento utilizado normalmente pelos ninjas de Hoshigakure, devido a vila ser cercada por um intenso gás venenoso após a queda do meteorito. Apesar disso, Kigiri e Hanzō também mostraram usar uma máscara semelhante a essa para efetuar suas técnicas que envolvem o uso explícito de gás.", "ガスマスク", "Gasumasuku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Naginata", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Nuibari", "A Nuibari (縫い針; 'Agulha Costuradora') é uma das espadas dos Sete Espadachins da Névoa, empunhada especialmente por Kushimaru Kuriarare. A própria lâmina assume a forma de uma estreita 'Espada Longa' (長刀, chōtō) que muito se assemelha a uma grande agulha, com um comprido fio de aço extremamente fino localizado na base do seu punho. Esta espada é dita possuir a capacidade de furar tudo e unir, demonstrado por meio de sua capacidade de penetrar facilmente alvos múltiplos em um único golpe e depois literalmente ponteá-los simplesmente usando o fio. Um usuário capaz pode até mesmo jogar a espada com a intenção de perfurar vários inimigos antes de pegar a agulha no outro lado e repetir o processo em diferentes ângulos, a fim de criar pontos de cadáveres com o fio passando por tudo, efetivamente criando fileiras de corpos em vários pontos ao longo do comprimento do fio.", "縫い針", "Nuibari", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Nunchaku", "O Nunchaku (ヌンチャク, Nunchaku) é uma arma ninja usada na série Naruto. Esta arma consistindo de duas varetas, geralmente feitas de aço, os quais estão ligados por uma corda ou corrente.", "ヌンチャク", "Nunchaku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ocarina", "Menma era capaz de usar ninjutsu com sua ocarina tocando uma melodia, aumentando a força e a velocidade de um indivíduo. Ele pode liberar o genjutsu, usado pelo membro de sua família, Shiin.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Orelhas de Gato", "Esta ferramenta foi dada a Naruto, Sasuke, Sakura e para esgueirarem-se para a Fortaleza de Gato de Nekomata. Essas ferramentas exclusivas permitem que o usuário interaja com outros gatos como se fossem gatos em si, mas se as orelhas caíssem, eles iriam voltar a ser distinguíveis de gatos.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Eremita de Pedra", "A Palma Eremita de Pedra é um item muito valioso em Naruto: Path of the Ninja 2. Ele pode ser usado um número ilimitado de vezes e cura tanto chakra quanto estamina.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palácio do Rei Dragão", "Tomando a forma de uma caixa preta, ela pode ser usada para expor uma substância gasosa a uma pessoa, todo o seu corpo se torna petrificado, resultando em sua preservação por dez mil anos.", "竜宮城", "Ryūgūjō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Panaceia Cinábrio", "A Panaceia Cinábrio é uma droga em forma de pílula de cor vermelha que concede juventude eterna e poderes sobrenaturais ao ser ingerida.", "万能薬辰砂", "Man'nōyaku Shinsa", null, "Elixir do Mártir[2]", null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pano de Ligação", "Este pano é uma ferramenta comum utilizada em Sunagakure para a Técnica da Ligação de Pano que é usada para imobilizar um oponente completamente. O pano pode também ser usado em conjunto com a Etiqueta de Selamento, a fim de selar o oponente. Os shinobi também são capazes de manipular livremente o pano. No anime, o pano foi também utilizado como um meio para esconder uma fuga, bem como defesa contra ataques. E enquanto encharcado de água, revelou-se uma ferramenta eficiente contra os ninjutsu da Liberação de Calor.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Papel Explosivo", "Papéis explosivos (Kibaku Fuda, 起爆札) são pedaços de papel inscritos com uma escrita especial, centrando em torno do kanji para 'explodir' (爆, baku). Infundidos com chakra, eles irão explodir depois de um determinado período de tempo, remotamente, ou depois de terem sido incendiados por uma chama. Os selos podem ser anexados a uma superfície ou em volta de uma arma para ser jogado em um inimigo. Eles são muito versáteis e uma das ferramentas básicas do ninja. Os ninjas podem confundir seus inimigos através da obtenção de selos explosivos falsos. Eles funcionam mesmo sob forte chuva.", "起爆札", "Kibaku Fuda", null, null, null, "Selo Explosivo, Papel Bomba");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Papel Sensível", "O Papel Sensível (感応紙, Kan no Kami; TV Brasileira 'Papel de Chakra') é um papel que permite identificar o tipo elemental de chakra de um ninja. Este papel é criado de árvores que por muito tempo, receberam algum tipo de chakra, até transformar-se em papéis. Segundo Kakashi, este é um método único e eficaz de identificar qual o tipo de chakra de alguma pessoa, observando que o ninja apenas precisa concentrar uma quantidade pequena de seu chakra em suas mãos que, automaticamente irá transmitir ao papel e obter o resultado.", "感応紙", "Kan no Kami", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pedra de Gelel", "A Pedra da Gelel é uma fonte de energia vital contida em um mineral visto em Naruto o Filme: Lenda da Pedra Gelel.", "ゲレルの石", "Gereru no Ishi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Perfume da Flor da Lua Nova", "O Perfume da Flor da Lua Nova foi criado por Tsukino a partir dos campos das Flores da Lua Nova em Tsukigakure. Este perfume dissipa genjutsu e hipnose. Naruto usou esse perfume em Tsukino para dissipar o Jibakugan dela.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho", "Pergaminhos (巻物, Makimono) continuam a ser uma das peças mais importantes do equipamento no arsenal de um ninja. Devido ao seu tamanho, múltiplos pergaminhos podem ser trazidos para a batalha nas bolsas de frente de um Colete ou em uma bolsa comum. Além de seu uso como um meio de literatura, o pergaminho também pode ser usado por ninjas para invocar criaturas, pessoas e itens em seu auxílio. Isto irá poupar espaço e evitar que o sacrifício de um pedaço muito necessário do equipamento que pode ser necessário mais tarde. Algumas técnicas podem também ser armazenadas neles.", "巻物", "Makimono", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho da Lua Vermelha", "O Pergaminho da Lua Vermelha é um pergaminho especial que pode acabar com o Mundo do Genjutsu.", "朱月の書", "Shugetsu no Sho", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho de Contrato", "Este pergaminho gigante é usado para fazer o contrato de invocação entre um humano e um sapo do Monte Myōboku.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho de Diagrama Alterador de Terreno", "Fuen usa esse pergaminho como um meio para a sua Técnica de Alteração do Terreno. Após a rocha ser convocada, Fuen pode controlar a rocha, mudando a sua imagem, permitindo-lhe inserir armadilhas ou criar mecanismos de monitoramento representados por pontos vermelhos em seu desenho. Ela também pode usá-lo como um meio para a técnica de Fudō.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho de Invocação da Cobra", "Depois de Sasuke colocar Hebi para procurar Itachi, foi dado um pergaminho especial para Suigetsu com o qual ele foi capaz de invocar cobras, apesar de não ter um contrato com elas. Suigetsu usaria um pouco de sangue de Sasuke, que após absorver Orochimaru, ganhando a habilidade de invocar cobras para si mesmo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho de Invocação de Clones da Sombra", "O clone é invocado.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pergaminho dos Selos", "O Pergaminho dos Selos (封印の書, Fūin no Sho) é um pergaminho contendo várias técnicas, por exemplo a Técnica dos Múltiplos Clones da Sombra e o Estilo de Selamento de Oito Trigramas. Devido às técnicas perigosas que ele contém, o pergaminho foi selado pelo Primeiro Hokage Hashirama Senju.", "封印の書", "Fūin no Sho", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pesos de Treinamento", "Os Pesos de Treinamento são unidades que causam pressão no usuário, sendo bastante utilizado em métodos de treinamento antigos. Esses pesos foram inicialmente inseridos na série como meio de treinamento em que Might Guy submeteu Rock Lee, fazendo-o utilizar eles em seus dois calcanhares a fim de ampliar gradativamente sua velocidade, mobilidade e aspecto físico.", "修行重り", "Shugyō Omori", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pinças de Tesoura", "Estes tipos de pinças de tesoura foram empunhadas por Kamikiri do clã Fūma. A mola das pinças é conectada ao seu braço e bíceps direito por uma faixa preta. Usando um fio branco amarrado à base de cada lâmina e na mão, lhe permite usar os excessivamente grandes pinças, sem muito esforço ou usando as duas mãos. Kamikiri usa essas pinças regularmente de seu estilo de luta para apertar e cortar adversários dentro do seu campo de ataque, além de uma arma para atacar seu oponente e capaz de até mesmo quebrar através de rocha sólida.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Planta Medicinal para Olho", "A Planta Medicinal para Olho (目薬草, Megusurisō) é uma erva medicinal rara que pode ser encontrada no Vale do Julgamento e é dita sendo ótima para fadiga muscular.", "目薬草", "Megusurisō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pomada Secreta do Clã Hyūga", "A pomada curando as feridas de Naruto.", null, null, null, "Pomada (塗り薬, Nurigusuri)", null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Porrete", null, null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Protetor de Testa", "O Protetor de Testa (額当て, Hitai-ate) é a faixa que é usada por quase todo ninja, composto por uma placa de metal gravada com o símbolo da vila oculta daquele ninja, e normalmente por um tecido que pode ser de diferentes cores e tamanhos. O protetor de testa é recebido uma vez que um ninja gradua-se na Academia e ninjas de nível genin ou mais alto são os únicos autorizados a te-los e usá-los.", "額当て", "Hitai-ate", null, null, null, "Bandana");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pó de Fogo Azul", "Pó de Fogo Azul (青火粉, Aobiko) é uma substância especial que cria uma grande explosão quando exposto à água. Ele foi descoberto acidentalmente pelo clã Kumanoi da Vila do Uivo do Lobo quando eles estavam tentando criar um medicamento que iria conceder a juventude eterna. O uso de fogo azul em pó pode aumentar o nível significativamente do campo de jogo, mesmo os não-shinobi são capazes de competir com shinobis treinados ou mesmo uma força mais fraca pode derrotar uma força superior, como quando a Vila do Uivo do Lobo derrotou Amegakure. A venda do pó de fogo azul trouxe grande riqueza para o clã Kumanoi e para o resto da Vila do Uivo do Lobo, permitindo a aldeia deixar de ser uma aldeia escondida.", "青火粉", "Aobiko", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pó de Ouro", "Pó de Ouro (砂金, Sakin) é o nome da arma usada pelo Quarto Kazekage. Devido às propriedades diamagnéticas do ouro, pequenas partículas dele podiam ser manipuladas numa maneira parecida com a da areia utilizada pelo jinchūriki do Shukaku, através da kekkei genkai, a Liberação de Magnetismo do Kazekage. Quando ele usava essa habilidade, anéis negros apareceriam ao redor de seus olhos que lembram os permanentes de seu filho mais novo, Gaara.", "砂金", "Sakin", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pílula de Aumento de Sangue", "A Pílula de Aumento de Sangue (増血丸, Zōketsugan) é um comprimido usado por shinobi para acelerar a formação e aumento de sangue no corpo. Depois de ter perdido uma quantidade significativa de sangue na batalha, um ninja pode usar essa pílula para repor o sangue dele, para que possa continuar lutando.", "増血丸", "Zōketsugan", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pílula do Despertar da Mente", "A Pílula do Despertar da Mente (醒 心 丸, Seishingan) é um medicamento criado por Orochimaru para acelerar o desenvolvimento do Selo Amaldiçoado e para aumentar o nível de Selo Amaldiçoado do usuário para o nível 2. No entanto, isso vai aumentar consideravelmente a velocidade de contaminação do Selo Amaldiçoado, matando o utilizador em poucos minutos. Para permitir que o corpo do usuário se acostume com o Selo Amaldiçoado Nível 2, eles têm que ser confinados dentro da Formação dos Quatro Nevoeiros Pretos. Isto irá reduzir a morte permanente a um estado de morte falsa.", "醒心丸", "Seishingan", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pílulas de Genjutsu", "Essas pílulas especiais foram desenvolvidos por Orochimaru. Uma vez ingeridas, as pílulas irão lançar um genjutsu sobre o alvo que paralisa o corpo. As drogas também afetam a mente do alvo, até certo ponto, como Gyūki teve problemas para se lembrar de seu último tumulto após Blue B tomar as pílulas.", "幻術屋久", "Genjutsu Yaku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pílulas de Ração Militar", "Pílulas de Ração Militar (兵粮丸, Hyōrōgan) são pílulas especiais que reabastecem o chakra de alguém e nutrem o corpo. Ela é composta de estimulantes poderosos e nutrientes, dizem que permite ao utilizador continuar a lutar por três dias e três noites sem descanso.", "兵糧丸", "Hyōrōgan", null, null, null, "Pílulas de Comida");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pássaro Mecânico", "Um Pássaro Mecânico semelhante a um brinquedo de corda que Chiyo mantém armazenado em um pergaminho. Ela usa esta ave mecânica para enviar informações de volta para Sunagakure. Após girar a manivela em suas costas, a ave toma voo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Receptor Negro", null, "黒い受信機", "Kuroi Jushinki", "Receptor Negro", "Agulha Negra (黒針, Kokushin)", null, "Receptores de Chakra");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rádio", "Rádio (無線, Musen) é um equipamento muito comum usado para manter contanto entre os ninja aliados através de grandes distâncias. Este rádio pode aceitar múltiplas frequências e, para ativá-lo, é necessário apertar o botão que fica anexado ao pescoço do utilizador.", "無線", "Musen", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sōshūga", "Sōshūga (双襲牙; Literalmente significa 'Presas Gêmeas Atacantes') é o nome de um par de nunchaku que são usados por Might Guy. Cada alça dessas armas está esculpida na forma de uma criatura semelhante a um Dragão chinês, com escalas gravadas ao longo de seu comprimento e uma cabeça perto da alça, enquanto parecem ser ligados entre si por uma corda torcida comprida. Might Guy empunha eles com grande destreza, utilizando todos os seus membros, às vezes, fazendo uso deles particularmente em situações onde o contato físico direto deve ser evitado.", "双襲牙", "Sōshūga", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sabre de Chakra da Luz Branca", "O Sabre de Chakra da Luz Branca foi um Tantō que originalmente pertencia a Sakumo Hatake, que passou para seu filho, Kakashi. Foi um tantō que possuía uma lâmina reta com um pequeno circulo à guarda mão. Ele exalava um traço branco de chakra, quando era balançado, dando assim a Sakumo o título de 'Presa Branca da Folha' (木ノ葉の白い牙, Konoha no Shiroi Kiba). Esta lâmina foi destruída durante a Terceira Guerra Mundial Shinobi, durante a luta de Kakashi contra Kakkō. No anime, mais tarde foi mostrado que a arma foi reparada e que Kakashi ainda a usava em missões.", "白光チャクラ刀", "Hakkō Chakura Tō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Saezuri", "Saezuri (囀り) é um dispositivo de som antigo utilizado para obter a potência da Ama no Hoko. A activação e controle do dispositivo requerem combinações de sete sons. Estes sons são produzidos através da inserção e ativação de sete barras de metal, os quais são referidos como 'Sons', para dentro do dispositivo. Além do padrão de ativação, um segundo padrão de som é necessário para libertar a energia da Ama no Hoko.", "囀り", "Saezuri", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Salamandra", "Salamandra (山椒魚, Sanshōuo) é uma marionete, originalmente criada por Sasori, mas passou para Kankurō. Ao contrário de outras marionetes de Kankurō, a Salamandra permanece fiel ao seu nome e se assemelha a uma salamandra. Salamandra é uma marionete que tem alto poder defensivo. Tem um babado que oscila para defender-se contra ataques de frente, assim como a cauda, que é capaz de dobrar até atingir o babado e defende a parte traseira seguida por um manto de ferro imediatamente cobrindo todos os lados, com o corpo oco. Kankurō e até duas outras pessoas podem se defender contra ataques vindos de outras direções. Seu ponto fraco é a articulação ao lado do babado, que quando atingido vai soltar o escudo, tornando-o indefeso. Kankurō mais tarde reparou ela, depois de ser desmontada por Sasori. Durante a batalha dos esquadrões de emboscada, ela foi usada para prender Shin, mas foi explodido por uma das argilas explosivas de Deidara que estava dentro do estômago do Shin, embora tenha abafado a explosão.", "山椒魚", "Sanshōuo", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Samehada", "Samehada (鲛肌; Literalmente significa 'Pele de Tubarão') é uma grande espada senciente, comparável em tamanho ao Kubikiribōchō. Ela é descrita como 'a mais terrível de todas as sete espadas da névoa' e até ganhou o título de 'Grande Espada' (大刀, Daitō), acrescentando ainda mais fama em sua reputação temível.", "鮫肌", "Samehada", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sasori (marionete)", "Escorpião era o corpo marionete de Sasori quando ele estava vivo. Quando ele a habitava, continha o seu 'núcleo de carne viva' (生身の核, namami no kaku) no compartimento esquerdo em seu peito.", "蠍", "Sasori", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Barreira", "Estes selos são peças especiais de papel com a inscrição kanji para selar ou restringir (结, ketsu), que é 'barreira' (结界, kekkai). O selo forma uma barreira para proteger o que quer que está localizado no seu interior. Quando este selo foi usado, um shinobi estava posicionado acima dela, o que indica que a técnica pode exigir a manutenção.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Chakra", "O Selo de Chakra é uma etiqueta especial capaz de restringir chakra e influências negativas. Ela foi desenvolvida por Jiraiya, para que ele pudesse suprir a influência do Nove-Caudas.", "チャクラの封", "Chakura no Fū", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Senbon", "As Senbon são agulhas de metal com um ponto nas duas extremidades. Muitas vezes podem ser usadas em finalidades médicas, para atacar os pontos de acupuntura. Apesar de serem pouco usadas para matar, um usuário com conhecimentos médicos adequados pode usar o Senbon no campo de batalha para incapacitar ou até matar o alvo. Por vezes, para aumentar a efeito do Senbon o usuário põe veneno nas extremidades. Um dos benefícios do Senbon é ser menor que uma kunai ou shuriken, tornando-o mais difícil de ver e esquivar. Como demonstrado por Shikamaru Nara nos Exames Chūnin, o ninja também pode anexar sinos à senbon para enganar seu oponente.", "千本", "Senbon", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Serra Circular de Marionete", "Esta é uma arma que Sasori esconde no braço esquerdo do boneco do Terceiro Kazekage, da qual é revelada depois do uso da Manipulação da Força de Mil Mãos.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shakujō", "A Shakujō (錫杖, Literalmente significa: Cajado do Monge) é um cajado budista, geralmente com anéis, usado em orações ou como uma arma.", "錫杖", "Shakujō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shibuki (espada)", "A Shibuki (飛沫; 'Respingo') é uma das espadas dos Sete Espadachins Ninja da Névoa, empunhada por Jinpachi Munashi. A lâmina utiliza vários papéis explosivos, contidos dentro de um deslocamento substancial de modo que incorpora explosões junto ao Kenjutsu do usuário, ganhando o título de 'Espada Explosiva' (爆刀, bakutō). A espada em si possui dois lados distintos, com uma aresta de corte estreito localizado abaixo de uma ampla plataforma. O rolo acima se desenrola ao longo deste último lado, deslizando para baixo em cima de uma superfície para formar um segmento individual composto de inúmeras marcas de explosivo que se separa do restante após a detonação. A espada é capaz de recarregar um outro segmento de etiquetas para a plataforma automaticamente após cada explosão.", "飛沫", "Shibuki", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shichiseiken", "A Shichiseiken (七星剣; Literalmente significa 'Espada de Sete Estrelas') é uma espada azul claro larga e plana com um talismã marrom pendurado na parte inferior. Ao dizer o comando verbal 'maldição' (呪え, noroe), pode ser usada para cortar a palavra alma (言霊, kotodama) dos atingidos pela Kōkinjō, permitindo que a alma seja sugada para dentro da Benihisago. Completando as condições da 'maldição' da vítima, se pronunciar a palavra que ela mais disse durante sua vida, ela vai ser sugada e presa dentro da Benihisago. O que quer que esta palavra seja será inscrita para a lâmina da Shichiseiken. O portador pode dobrar a lâmina para evitar que a vítima veja o que é. Se houver uma mudança na palavra mais utilizada em qualquer ponto, a palavra inscrita será atualizada.", "七星剣", "Shichiseiken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shinki Demoníaco", "Shinga fundido com a arma.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuigu", "Shuigu (シュイグ, Shuigu) é uma ferramenta transmitida por gerações dentro da família real do País Redaku.", "シュイグ", "Shuigu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuriken", "Shuriken (手裏剣), assim como a kunai, é uma das armas mais básicas vistas na série Naruto. Elas são estrelas afiadas com quatro pontas de metal, úteis para jogar. Elas também podem ser utilizadas para o combate próximo, mas não são muito eficientes. Embora pequena, ela pode ser usada para distrair, prender o inimigo no solo, ou, precisando o suficiente, pode matar ou incapacitar um inimigo. Ela tem um círculo aberto no centro, útil para agarrar com um dedo, para evitar o corte de si mesmo, ou para colocá-la através de um fio. O círculo também é benéfico para a aerodinâmica total da arma, garantindo trajetórias mais precisas.", "手裏剣", "Shuriken", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sino", "Sino é uma pequena bola oca de metal com um algum material metálico menor dentro que serve para tinir o som. A fim de carregá-lo na cintura ou para táticas de batalha, ele possui um cordão, que geralmente possui a cor vermelha quando usado em treinamento ou um Fio de Aço para ser usado em táticas de batalha.", "鈴", "Suzu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sino da Sacerdotisa", "O Sino da Sacerdotisa foi um presente dado a Shion por sua mãe para protegê-la do perigo.", "巫女の鈴", "Miko no Suzu", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Snowboard", "O Snowboard é uma ferramenta usada para movimentos rápidos e fáceis ao longo de um ambiente de neve. É fácil também de manobrar em uma floresta de neve. É vermelho na borda e tem marcas roxas no interior. Ele tem dois furos com uma corda por baixo para segurar depois do desembarque.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tambor Mecânico da Formação de Oito Trigramas", "Um tambor gigante, especificamente, um ōdaiko, um pouco mais alto do que Tsunade em diâmetro, usado na Formação do Mecanismo de Oito Trigramas de Konohagakure Quando utilizado, o Ōdaiko é mantido em uma posição horizontal no topo da Torre Hokage. O usuário bate no Ōdaiko para ativar a técnica, a fim de prender o inimigo em um labirinto de paredes. Cada batida subseqüente irá ativar ou desativar mais paredes.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tantō", "A Tantō é simplesmente uma pequena katana. Vários chūnin, iryō-nin e shinobi de Konohagakure usam-na amarrada inferiormente em suas costas quando vão para a batalha. A versão que a Raiz utiliza é diferente, tendo dois gumes e não tendo ponta, destinado somente para efetuar cortes e utilizar como meio de defesa. Eles carregam a tantō verticalmente na parte traseira especificamente por trás do ombro, Sai é um dos notáveis usuários de tantō, na qual ele carregava nas costas por trás do ombro direito. Sakumo e Kakashi Hatake — após ter herdado isso — usam uma tantō especial, conhecida como Sabre de Chakra da Luz Branca. Gamabunta e Gamakichi também carregam uma, embora em versões gigantescas que se encaixam em seu tamanho.", "短刀", "Tantō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tekkō Kagi", "As Tekkō Kagi são luvas ou manoplas com lâminas retráteis sobre os nós dos dedos. Com as lâminas de fora, os membros da Família Kurosuki foram capazes de usar seu Tornado Negro.", "手甲鉤", "Tekkō Kagi", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tenseigan de Hamura", "O Tenseigan de Hamura, também conhecido como o Recipiente de Energia (エネルギー体, Enerugī-tai), é um tesouro sagrado do Clã Ōtsutsuki, que havia sido usado por milênios para selar o Byakugan do clã para combinar seu poder. Ele é um altar usado para recolher e amplificar a energia natural, com o clã e usá-la como fonte de energia para tudo na lua, incluindo as inúmeras marionetes autônomos que serviram-lhes de potência. Como tal, tem o poder de criar explosões em massa de chakra que pode aniquilar exércitos inteiros em uma maneira similar a Bola da Besta com Cauda. Diz-se que o recipiente só pode ser tocado por aqueles com o 'chakra não diluído de Hamura', ou pelo clã Ōtsutsuki e Hyūga, ambos dos quais são descendentes de Hamura.", "ハムラの転生眼", "Hamura no Tenseigan", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Terceiro Kazekage (marionete)", "Esta é a marionete criada por Sasori do corpo do Terceiro Kazekage de Sunagakure. Os esforços que Sasori teve que passar para adquirir a marionete fez dela a sua favorita. No final, Sakura Haruno destruiu o boneco e, no anime, quando Chiyo tentou usar a cabeça para atacar Sasori, ele simplesmente incinerou-o.", "三代目風影", "Sandaime Kazekage", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tobishachimaru", "O Tobishachimaru (飛鯱丸, Tobishachimaru) é um navio voador projetado por Tazuna e criado pelos carpinteiros do País das Ondas. Esperava-se que ele fosse o primeiro de uma frota de navios voadores, o que iria revolucionar o transporte de mercadorias entre os países. Batizado com o nome orca, o Tobishachimaru tem 223 metros de comprimento, 34 metros de diâmetro, tem uma velocidade máxima de 70 km/h, e destina-se a voar a uma altitude de 5.000 metros. Ele usa balões cheios de hélio para flutuação e seis hélices para o movimento e direção. Além da carga e da tripulação, ele pode acomodar cerca de sessenta passageiros, em cujo benefício o Tobishachimaru é equipado com uma cozinha, compartimento de jantar, e outras amenidades.", "飛鯱丸", "Tobishachimaru", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Traje de Lodo", "Nurari usa um terno com uma substância viscosa desconhecida que parece ser uma membrana de musgo. O terno permite Nurari fazer um número de técnicas, como a Massa de Água Viscosa, criar outro membro a partir do lodo ou usar a viscosidade do lodo para encurralar o adversário. O terno também aumenta seu poder de ataque e pode camuflar a sua presença. Hinata afirmou que todo o 'corpo' de Nurari é uma técnica e que está cheio de chakra. Ela também descreve o lodo como Visgo.", null, null, null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Transformação: Bastão Adamantino", "A técnica de transformação do Rei Macaco, Enma, é caracterizada pela dureza do diamante e de sua capacidade à vontade para estender e/ou expandir o Bastão Adamantino, que é a arma de escolha de Hiruzen Sarutobi. Este bastão, quando em uso, foi capaz de destruir facilmente as estruturas de madeira criadas por Hashirama, um usuário de Liberação de Madeira, e também para esmagar Kurama, mandando-o para as fronteiras de Konoha, e destruir os galhos da Árvore Divina.", "変化・金剛如意へんげ・こんごうにょい", "Henge: Kongōnyoi", "Transformação: Bastão Adamantino", null, "Transformação: Bastão Indestrutível", "Bastão Adamantino");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Três Pílulas Coloridas", "As Três Pílulas Coloridas são medicamentos secretos usados pelo clã Akimichi.", "三色の丸薬", "Sanshoku no Gan'yaku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tsurukame", "É um protótipo de escudo que foi criado por Iō. Ele contém um mecanismo interno de disparo para ferramentas ninja como manriki-gusari, bombas e senbon. Se a Tsurukame fica sem ferramentas, o portador pode recarregá-la através da inserção de um pergaminho contendo mais ferramentas em um compartimento na parte de trás.", "鶴亀", "Tsurukame", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Branca Secreta: A Coleção de Dez Marionetes de Chikamatsu", "Depois de desenrolar o pergaminho, a coleção de Chikamatsu aparece. Usando a Técnica de Marionetes simples, pode-se controlar à vontade, controlando dez marionetes ao mesmo tempo em cada dedo, com um poder correspondente a mil bonecos juntos. Exige conhecimentos de alta-classe e habilidade, para conseguir controlá-los em seu auge de poder.", "白秘技・十機近松の集", "Shirohigi: Jikki Chikamatsu no Shū", "Técnica Branca Secreta: A Coleção de Chikamatsu das Dez Marionetes", "Dez Marionetes de Chikamatsu", "Técnica Branca Secreta: Dez Obras-Primas de Chikamatsu", "Ataque Branco Secreto: As Dez Marionetes de Chikamatsu");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Técnica Vermelha Secreta: Performance das Cem Marionetes", "Inicialmente, Sasori remove um pergaminho de invocação de suas costas. Ele então abre seu compartimento no peito direito emitindo mais de uma centena de cordas chakra, que atingem o pergaminho. Desde o pergaminho, mais de cem marionetes vestindo mantos vermelhos emergem.", "赤秘技・百機の操演", "Akahigi: Hyakki no Sōen", "Técnica Secreta Vermelha: Performance das Cem Marionetes", null, null, "Ataque Vermelho Secreto: Atuação de Cem Marionetes");
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Veneno", "Venenos, toxinas e peçonhas são todas substâncias venenosas inebriantes — pós, líquidos e gases — que podem ser usados ​​pelos ninja em combate. Venenos contam com uma variedade de efeitos, desde a fraqueza corporal, paralisia ou morte.", "毒", "Doku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Veneno Mortal", "O Veneno Mortal é um tóxico mortal utilizado nas armas de Sasori.", "劇毒", "Gekidoku", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Zanbatō", "Esta é uma lâmina gigante e larga, com três dobradiças que correm ao longo da própria lâmina. Devido suas propriedades para corte de animais, como cavalos, esta espada é bastante pesada e afiada.", "斬馬刀", "Zanbatō", null, null, null, null);
INSERT INTO `tools` (`id`, `name`, `description`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Água do Herói", "Água do Herói (英雄 の 水, Eiyu no Mizu) é uma substância exclusiva de Takigakure que permite a um ninja possuir pelo menos um aumento de dez vezes no nível de chakra por um curto período de tempo. Em troca desta energia, a água diminui a duração da vida do usuário proporcionalmente pelo número de vezes que o seu chakra foi aumentado, como no caso do pai de Shibuki, que morreu devido aos efeitos colaterais. O aumento do chakra é tão potente que, como foi demonstrado com Suien, o usuário pode criar um escudo de chakra para desviar projéteis, semelhante aos Oito Trigramas Palmas Giratórias do Céu. A água é produzida uma vez a cada cem anos na árvore gigante que esconde a aldeia.", "英雄の水", "Eiyu no Mizu", null, null, null, null);